import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useMemo } from 'react';
import {
    Dimensions,
    Image,
    SafeAreaView,
    ScrollView,
    StyleSheet,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'HotelDetail'>;

const { width } = Dimensions.get('window');

const HotelDetailScreen = ({ route, navigation }: Props) => {
    const { hotelId, hotelName } = route.params;

    // Ê®°ÊãüÊàøÂûãÊï∞ÊçÆ
    const roomTypes = [
        { id: 'b', name: 'Ë°åÊîøÂ§ßÂ∫äÊàø', price: 899, status: 'Á¥ß‰øè', info: 'Â§ßÂ∫ä | ‰∏çÂê´Êó©' },
        { id: 'a', name: 'Ê†áÂáÜÂèå‰∫∫Êàø', price: 599, status: 'ÊúâÊàø', info: 'ÂèåÂ∫ä | Âê´ÂèåÊó©' },
        { id: 'c', name: 'Ë±™ÂçéÊÄªÁªüÂ•óÊàø', price: 2599, status: '‰ªÖÂâ©1Èó¥', info: 'ÁâπÂ§ßÂ∫ä | Â∞ä‰∫´Ë°åÊîøÁ§ºÈÅá' },
    ];

    // 1. Ê®°Êãü Banner ÂõæÁâáÂàóË°®
    const bannerImages = [
        'https://via.placeholder.com/800x450/448aff/ffffff?text=Hotel+Image+1',
        'https://via.placeholder.com/800x450/2ecc71/ffffff?text=Hotel+Image+2',
        'https://via.placeholder.com/800x450/e74c3c/ffffff?text=Hotel+Image+3',
    ];

    // 2. ‰ª∑Ê†º‰ªé‰ΩéÂà∞È´òÊéíÂ∫èÈÄªËæë
    const sortedRooms = useMemo(() => {
        return [...roomTypes].sort((a, b) => a.price - b.price);
    }, [roomTypes]);

    return (
        <View style={styles.container}>
            {/* 1. È°∂ÈÉ®Ëá™ÂÆö‰πâÂØºËà™Â§¥ */}
            <SafeAreaView style={styles.header}>
                <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
                    <Text style={styles.backIcon}>„Äà</Text>
                </TouchableOpacity>
                <Text style={styles.headerTitle} numberOfLines={1}>{hotelName}</Text>
                <View style={{ width: 40 }} />
            </SafeAreaView>

            <ScrollView bounces={false} showsVerticalScrollIndicator={false}>
                {/* 2. Â§ßÂõæ Banner (ÊîØÊåÅÊ®™ÂêëÊªöÂä®) */}
                <View style={styles.bannerContainer}>
                    <ScrollView
                        horizontal
                        pagingEnabled
                        showsHorizontalScrollIndicator={false}
                    >
                        {bannerImages.map((uri, index) => (
                            <Image key={index} source={{ uri }} style={styles.bannerImage} />
                        ))}
                    </ScrollView>
                    <View style={styles.imageCountBadge}>
                        <Text style={styles.imageCountText}>1/{bannerImages.length}</Text>
                    </View>
                </View>

                {/* 3. ÈÖíÂ∫óÂü∫Á°Ä‰ø°ÊÅØÂëàÁé∞ */}
                <View style={styles.infoSection}>
                    <View style={styles.row}>
                        <Text style={styles.hotelTitle}>{hotelName}</Text>
                        <Text style={styles.starText}>È´òÊ°£Âûã</Text>
                    </View>
                    <Text style={styles.tags}>ÂÖçË¥πWiFi ¬∑ Ë°åÊùéÂØÑÂ≠ò ¬∑ 24Â∞èÊó∂ÁÉ≠Ê∞¥</Text>
                    <View style={styles.addressContainer}>
                        <Text style={styles.addressText}>üìç ÊüêÊüêÁúÅÊüêÊüêÂ∏Ç‰∏≠ÂøÉÂïÜÂä°Âå∫ 88 Âè∑</Text>
                    </View>
                </View>

                {/* 4. Êó•ÂéÜ+Èó¥Â§ú Banner */}
                <TouchableOpacity style={styles.calendarBanner}>
                    <View style={styles.calendarInfo}>
                        <View>
                            <Text style={styles.dateText}>05Êúà01Êó• <Text style={styles.weekDay}>Âë®‰∏Ä</Text></Text>
                            <Text style={styles.subDateText}>ÂÖ•‰Ωè</Text>
                        </View>
                        <View style={styles.nightBadge}>
                            <Text style={styles.nightText}>1Êôö</Text>
                        </View>
                        <View>
                            <Text style={styles.dateText}>05Êúà02Êó• <Text style={styles.weekDay}>Âë®‰∫å</Text></Text>
                            <Text style={styles.subDateText}>Á¶ªÂ∫ó</Text>
                        </View>
                    </View>
                    <Text style={styles.arrowText}>‰øÆÊîπ „Äâ</Text>
                </TouchableOpacity>

                {/* 5. ÈÖíÂ∫óÂΩìÂâçÊàøÂûã‰ª∑Ê†ºÂàóË°® (Â∑≤ÊéíÂ∫è) */}
                <View style={styles.roomSection}>
                    {sortedRooms.map(room => (
                        <View key={room.id} style={styles.roomCard}>
                            <View style={styles.roomInfoLeft}>
                                <Text style={styles.roomNameText}>{room.name}</Text>
                                <Text style={styles.roomDetailText}>{room.info}</Text>
                                <Text style={[styles.statusTag, { color: room.status === 'ÊúâÊàø' ? '#52c41a' : '#ff4d4f' }]}>
                                    {room.status}
                                </Text>
                            </View>
                            <View style={styles.roomPriceRight}>
                                <View style={styles.priceRow}>
                                    <Text style={styles.currency}>Ôø•</Text>
                                    <Text style={styles.priceNumber}>{room.price}</Text>
                                </View>
                                <TouchableOpacity style={styles.bookButton} onPress={() => alert(`È¢ÑÂÆö ${room.name}`)}>
                                    <Text style={styles.bookButtonText}>È¢ÑÂÆö</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    ))}
                </View>

                {/* Â∫ïÈÉ®ÁïôÁôΩÔºåÈò≤Ê≠¢ÂÜÖÂÆπË¢´ÈÅÆÊå° */}
                <View style={{ height: 40 }} />
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, backgroundColor: '#f5f7fa' },
    // ÂØºËà™Â§¥
    header: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        backgroundColor: '#fff',
        paddingHorizontal: 10,
        borderBottomWidth: StyleSheet.hairlineWidth,
        borderBottomColor: '#ddd'
    },
    backButton: { width: 40, paddingVertical: 10 },
    backIcon: { fontSize: 20, fontWeight: '300', color: '#333' },
    headerTitle: { fontSize: 17, fontWeight: 'bold', color: '#333', flex: 1, textAlign: 'center' },

    // Banner
    bannerContainer: { width: width, height: 220, backgroundColor: '#ccc' },
    bannerImage: { width: width, height: 220, resizeMode: 'cover' },
    imageCountBadge: {
        position: 'absolute', bottom: 12, right: 12,
        backgroundColor: 'rgba(0,0,0,0.6)', paddingHorizontal: 8, paddingVertical: 2, borderRadius: 10
    },
    imageCountText: { color: '#fff', fontSize: 12 },

    // Âü∫Á°Ä‰ø°ÊÅØ
    infoSection: { backgroundColor: '#fff', padding: 16, marginBottom: 10 },
    row: { flexDirection: 'row', alignItems: 'flex-end', marginBottom: 8 },
    hotelTitle: { fontSize: 20, fontWeight: 'bold', color: '#333', marginRight: 8 },
    starText: { fontSize: 12, color: '#666', marginBottom: 2 },
    tags: { fontSize: 13, color: '#1890ff', marginBottom: 10 },
    addressContainer: { borderTopWidth: 0.5, borderTopColor: '#f0f0f0', paddingTop: 10 },
    addressText: { fontSize: 14, color: '#444' },

    // Êó•ÂéÜÈó¥Â§ú
    calendarBanner: {
        flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between',
        backgroundColor: '#fff', marginHorizontal: 10, padding: 12, borderRadius: 8, marginBottom: 10,
        // Èò¥ÂΩ±
        shadowColor: '#000', shadowOffset: { width: 0, height: 1 }, shadowOpacity: 0.1, shadowRadius: 2, elevation: 2
    },
    calendarInfo: { flexDirection: 'row', alignItems: 'center' },
    dateText: { fontSize: 16, fontWeight: 'bold' },
    weekDay: { fontSize: 12, fontWeight: 'normal', color: '#999' },
    subDateText: { fontSize: 11, color: '#999', marginTop: 2 },
    nightBadge: { marginHorizontal: 15, paddingHorizontal: 8, borderBottomWidth: 1, borderBottomColor: '#eee' },
    nightText: { fontSize: 12, color: '#333' },
    arrowText: { color: '#1890ff', fontSize: 13 },

    // ÊàøÂûãÂàóË°®
    roomSection: { paddingHorizontal: 10 },
    roomCard: {
        flexDirection: 'row', backgroundColor: '#fff', borderRadius: 8, padding: 15, marginBottom: 10,
        justifyContent: 'space-between', alignItems: 'center'
    },
    roomInfoLeft: { flex: 1 },
    roomNameText: { fontSize: 16, fontWeight: 'bold', color: '#333', marginBottom: 4 },
    roomDetailText: { fontSize: 12, color: '#666', marginBottom: 6 },
    statusTag: { fontSize: 11, fontWeight: '500' },
    roomPriceRight: { alignItems: 'flex-end' },
    priceRow: { flexDirection: 'row', alignItems: 'baseline', marginBottom: 6 },
    currency: { fontSize: 14, color: '#ff4d4f', fontWeight: 'bold' },
    priceNumber: { fontSize: 22, color: '#ff4d4f', fontWeight: 'bold' },
    bookButton: { backgroundColor: '#ff9500', paddingHorizontal: 20, paddingVertical: 8, borderRadius: 4 },
    bookButtonText: { color: '#fff', fontWeight: 'bold', fontSize: 14 }
});

export default HotelDetailScreen;
