import { Dimensions, StyleSheet } from 'react-native';

const { width } = Dimensions.get('window');

export const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#F5F6F7',
    },
    // 1. 顶部核心筛选头
    headerContainer: {
        backgroundColor: '#fff',
        paddingTop: 10,
        paddingBottom: 5,
        borderBottomWidth: StyleSheet.hairlineWidth,
        borderBottomColor: '#EEE',
    },
    searchBar: {
        flexDirection: 'row',
        alignItems: 'center',
        backgroundColor: '#F2F2F2',
        marginHorizontal: 15,
        borderRadius: 8,
        paddingHorizontal: 12,
        paddingVertical: 8,
    },
    cityText: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#333',
        marginRight: 10,
    },
    dateInfo: {
        flex: 1,
        borderLeftWidth: 1,
        borderLeftColor: '#DDD',
        paddingLeft: 10,
    },
    dateRangeText: {
        fontSize: 12,
        color: '#333',
        fontWeight: '500',
    },
    nightText: {
        fontSize: 11,
        color: '#999',
    },
    
    // 2. 详细筛选区域 (吸顶)
    filterRow: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        paddingVertical: 12,
        backgroundColor: '#fff',
    },
    filterItem: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    filterText: {
        fontSize: 14,
        color: '#666',
    },
    arrowIcon: {
        fontSize: 10,
        marginLeft: 4,
        color: '#999',
    },

    // 3. 酒店列表项
    hotelItem: {
        flexDirection: 'row',
        backgroundColor: '#fff',
        marginHorizontal: 12,
        marginTop: 12,
        borderRadius: 12,
        padding: 12,
        // 阴影
        elevation: 2,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 1 },
        shadowOpacity: 0.1,
        shadowRadius: 4,
    },
    hotelImage: {
        width: 110,
        height: 140,
        borderRadius: 8,
    },
    infoArea: {
        flex: 1,
        marginLeft: 12,
        justifyContent: 'space-between',
    },
    hotelName: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#333',
        lineHeight: 22,
    },
    scoreRow: {
        flexDirection: 'row',
        alignItems: 'center',
        marginTop: 4,
    },
    scoreText: {
        fontSize: 14,
        color: '#007AFF',
        fontWeight: 'bold',
    },
    commentText: {
        fontSize: 12,
        color: '#666',
        marginLeft: 6,
    },
    addressText: {
        fontSize: 12,
        color: '#999',
        marginTop: 4,
    },
    tagRow: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 8,
    },
    hotelTag: {
        backgroundColor: '#F0F7FF',
        paddingHorizontal: 6,
        paddingVertical: 2,
        borderRadius: 4,
        marginRight: 6,
        marginBottom: 4,
    },
    hotelTagText: {
        fontSize: 10,
        color: '#007AFF',
    },
    priceRow: {
        flexDirection: 'row',
        alignItems: 'flex-end',
        justifyContent: 'flex-end',
    },
    priceSymbol: {
        fontSize: 14,
        color: '#FF5A5F',
        fontWeight: 'bold',
        marginBottom: 2,
    },
    priceText: {
        fontSize: 22,
        color: '#FF5A5F',
        fontWeight: 'bold',
    },
    priceUnit: {
        fontSize: 12,
        color: '#999',
        marginLeft: 2,
        marginBottom: 4,
    },
    footerLoading: {
        paddingVertical: 20,
        alignItems: 'center',
    }
});
