import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useCallback, useState } from 'react';
import {
    ActivityIndicator,
    FlatList,
    Image,
    SafeAreaView,
    StyleSheet,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'HotelList'>;

// 定义列表项数据类型
interface HotelItem {
    id: string;
    name: string;
    score: number;
    address: string;
    price: number;
    image: string;
    tags: string[];
}

const HotelListScreen = ({ route, navigation }: Props) => {
    const { city, startDate, endDate } = route.params;

    // 状态管理：列表数据、加载状态
    const [hotels, setHotels] = useState<HotelItem[]>([
        {
            id: '1',
            name: '精品悦享酒店',
            score: 4.9,
            address: '朝阳区建国路 88 号',
            price: 1280,
            image: 'https://via.placeholder.com/150',
            tags: ['亲子', '免费停车场']
        },
        {
            id: '2',
            name: '城市便捷驿站',
            score: 4.2,
            address: '海淀区中关村大街 10 号',
            price: 450,
            image: 'https://via.placeholder.com/150',
            tags: ['近地铁']
        }
    ]);
    const [isLoading, setIsLoading] = useState(false);

    // 模拟上滑加载更多逻辑
    const loadMoreData = () => {
        if (isLoading) return;
        setIsLoading(true);
        console.log('正在加载下一页数据...');
        // 模拟网络请求
        setTimeout(() => {
            setIsLoading(false);
        }, 1500);
    };

    // 渲染单个酒店卡片
    const renderHotelItem = useCallback(({ item }: { item: HotelItem }) => (
        <TouchableOpacity
            style={styles.hotelCard}
            activeOpacity={0.8}
            onPress={() => navigation.navigate('HotelDetail', {
                hotelId: item.id,
                hotelName: item.name
            })}
        >
            <Image source={{ uri: item.image }} style={styles.hotelImage} />
            <View style={styles.hotelInfo}>
                <View>
                    <Text style={styles.hotelName} numberOfLines={1}>{item.name}</Text>
                    <Text style={styles.hotelScore}>{item.score}分 · 豪华型</Text>
                    <Text style={styles.hotelAddress} numberOfLines={1}>{item.address}</Text>
                </View>

                <View style={styles.tagContainer}>
                    {item.tags.map(tag => (
                        <View key={tag} style={styles.tagLine}>
                            <Text style={styles.tagText}>{tag}</Text>
                        </View>
                    ))}
                </View>

                <View style={styles.priceContainer}>
                    <Text style={styles.priceUnit}>￥</Text>
                    <Text style={styles.priceText}>{item.price}</Text>
                    <Text style={styles.priceSuffix}>起</Text>
                </View>
            </View>
        </TouchableOpacity>
    ), []);

    return (
        <SafeAreaView style={styles.container}>
            {/* 1. 顶部核心条件筛选头 */}
            <View style={styles.headerFilter}>
                <View style={styles.searchSummary}>
                    <Text style={styles.cityText}>{city}</Text>
                    <Text style={styles.dateText}>{startDate} - {endDate}</Text>
                </View>
                <TouchableOpacity style={styles.filterBtn} activeOpacity={0.7}>
                    <Text style={styles.filterBtnText}>详细筛选 ▽</Text>
                </TouchableOpacity>
            </View>

            {/* 2. 酒店列表 */}
            <FlatList
                data={hotels}
                renderItem={renderHotelItem}
                keyExtractor={item => item.id}
                contentContainerStyle={styles.listContent}
                onEndReached={loadMoreData}
                onEndReachedThreshold={0.2}
                ListFooterComponent={
                    isLoading ? <ActivityIndicator style={{ margin: 20 }} color="#007AFF" /> : null
                }
            />
        </SafeAreaView>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, backgroundColor: '#F8F9FA' },
    // 顶部筛选区
    headerFilter: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: 16,
        paddingVertical: 12,
        backgroundColor: '#FFFFFF',
        elevation: 2,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
    },
    searchSummary: { flex: 1 },
    cityText: { fontSize: 16, fontWeight: 'bold', color: '#333' },
    dateText: { fontSize: 12, color: '#666', marginTop: 2 },
    filterBtn: {
        backgroundColor: '#F0F7FF',
        paddingHorizontal: 12,
        paddingVertical: 6,
        borderRadius: 15,
    },
    filterBtnText: { color: '#007AFF', fontSize: 13, fontWeight: '500' },

    // 列表内容
    listContent: { padding: 12 },
    hotelCard: {
        flexDirection: 'row',
        backgroundColor: '#FFFFFF',
        borderRadius: 12,
        marginBottom: 12,
        padding: 12,
        // 卡片阴影
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 1 },
        shadowOpacity: 0.05,
        shadowRadius: 5,
        elevation: 3,
    },
    hotelImage: { width: 100, height: 120, borderRadius: 8 },
    hotelInfo: { flex: 1, marginLeft: 12, justifyContent: 'space-between' },
    hotelName: { fontSize: 17, fontWeight: 'bold', color: '#1A1A1A' },
    hotelScore: { fontSize: 13, color: '#007AFF', marginVertical: 4 },
    hotelAddress: { fontSize: 12, color: '#999' },

    // 标签区
    tagContainer: { flexDirection: 'row', flexWrap: 'wrap', marginVertical: 6 },
    tagLine: {
        backgroundColor: '#F5F5F5',
        paddingHorizontal: 6,
        paddingVertical: 2,
        borderRadius: 4,
        marginRight: 6,
        marginBottom: 4,
    },
    tagText: { fontSize: 10, color: '#666' },

    // 价格区
    priceContainer: { flexDirection: 'row', alignItems: 'baseline', justifyContent: 'flex-end' },
    priceUnit: { fontSize: 12, color: '#FF4D4F', fontWeight: 'bold' },
    priceText: { fontSize: 22, color: '#FF4D4F', fontWeight: 'bold' },
    priceSuffix: { fontSize: 12, color: '#999', marginLeft: 2 },
});

export default HotelListScreen;
