import { Dimensions, StyleSheet } from 'react-native';

const { width } = Dimensions.get('window');

export const styles = StyleSheet.create({
    // ... 其他样式保持不变

    dateRow: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 12,
        // 确保整行子元素垂直居中
    },
    
    // 关键点：为包裹“入住”和“离店”的 View 添加对齐
    dateItem: {
        flex: 1,
        alignItems: 'center', // 1. 让容器内的子元素（Label和Date）水平居中
    },

    label: { 
        fontSize: 12, 
        color: '#999',
        textAlign: 'center',  // 2. 确保文字在自身宽度内居中（双保险）
    },

    dateText: { 
        fontSize: 18, 
        fontWeight: '600', 
        color: '#333', 
        marginTop: 4,
        textAlign: 'center',  // 3. 让日期数字也居中
    },
    locationRow: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 10,
        justifyContent: 'space-between', // 确保横向撑开
    },
    // 新增：用于包裹省、市的每一个单元格
    locationItem: {
        flex: 1, // 关键：三个部分平分空间
        justifyContent: 'center',
    },
    locationBtn: {
        flex: 1, // 关键：定位按钮也占 1/3
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center', // 居中显示定位图标和文字
        borderLeftWidth: 1,
        borderLeftColor: '#eee',
    },
    locationIcon: { 
        fontSize: 16, 
        marginRight: 4 
    },
    locationText: { 
        color: '#007AFF', 
        fontWeight: '500' 
    },
    
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    bannerContainer: {
        width: width,
        height: 180,
    },
    bannerImage: {
        width: '100%',
        height: '100%',
    },
    bannerMask: {
        position: 'absolute',
        bottom: 0,
        width: '100%',
        padding: 15,
        backgroundColor: 'rgba(0,0,0,0.3)',
    },
    bannerTitle: {
        color: '#fff',
        fontSize: 18,
        fontWeight: 'bold',
    },
    searchCard: {
        margin: 15,
        marginTop: -10,
        backgroundColor: '#fff',
        borderRadius: 12,
        padding: 16,
        elevation: 4,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 10,
    },
    // locationRow: {
    //     flexDirection: 'row',
    //     alignItems: 'center',
    //     paddingVertical: 10,
    // },
    // locationBtn: {
    //     flexDirection: 'row',
    //     alignItems: 'center',
    //     borderLeftWidth: 1,
    //     borderLeftColor: '#eee',
    //     paddingLeft: 10,
    // },
    // locationIcon: { fontSize: 16, marginRight: 4 },
    // locationText: { color: '#007AFF', fontWeight: '500' },
    // dateRow: {
    //     flexDirection: 'row',
    //     alignItems: 'center',
    //     paddingVertical: 12,
    // },
    // dateText: { fontSize: 18, fontWeight: '600', color: '#333', marginTop: 4 },
    weekText: { fontSize: 12, fontWeight: 'normal', color: '#999' },
    nightBadge: {
        borderWidth: 1,
        borderColor: '#eee',
        borderRadius: 10,
        paddingHorizontal: 8,
        height: 20,
        justifyContent: 'center',
    },
    nightText: { fontSize: 10, color: '#666' },
    searchInput: {
        fontSize: 16,
        padding: 0,
        marginTop: 4,
        color: '#333',
        fontWeight: '500',
    },
    tagScroll: {
        marginHorizontal: -16,
        paddingHorizontal: 16,
        marginTop: 15,
    },
    tagItem: {
        backgroundColor: '#f2f2f2',
        paddingHorizontal: 12,
        paddingVertical: 6,
        borderRadius: 4,
        marginRight: 8,
    },
    tagText: { fontSize: 12, color: '#666' },
    searchItem: { paddingVertical: 12 },
    // label: { fontSize: 12, color: '#999' },
    searchValue: { fontSize: 16, fontWeight: '600', color: '#333', marginTop: 4 },
    divider: { height: 1, backgroundColor: '#f0f0f0' },
    searchButton: {
        backgroundColor: '#007AFF',
        borderRadius: 8,
        height: 48,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 20,
    },
    searchButtonText: { color: '#fff', fontSize: 18, fontWeight: 'bold' },
    infoSection: { alignItems: 'center', marginVertical: 20 },
    infoText: { color: '#ccc', fontSize: 12 }
});
