import { NativeStackScreenProps } from '@react-navigation/native-stack';

import React, { useState } from 'react';
// ÂØºÂÖ•ÁúÅÁ∫ßÂíåÂú∞Á∫ßÊï∞ÊçÆ
import citiesRaw from 'china-division/dist/cities.json';
import provincesRaw from 'china-division/dist/provinces.json';
import {
    Alert,
    FlatList,
    Image,
    Modal,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';
import { styles } from './index.styles';


// ÊèêÂèñÁúÅ‰ªΩÂêçÁß∞ÂàóË°®
const provinceList = provincesRaw.map(p => p.name);

// ÊèêÂèñÂüéÂ∏ÇÊò†Â∞ÑÔºö { "Âπø‰∏úÁúÅ": ["ÂπøÂ∑ûÂ∏Ç", "Ê∑±Âú≥Â∏Ç", ...], "Âåó‰∫¨Â∏Ç": ["Âåó‰∫¨Â∏Ç"] }
const cityMap: Record<string, string[]> = {};
provincesRaw.forEach(p => {
    // ËøáÊª§Âá∫Â±û‰∫éËØ•ÁúÅÁöÑÊâÄÊúâÂüéÂ∏Ç
    const matchedCities = citiesRaw
        .filter(c => c.provinceCode === p.code)
        .map(c => c.name);
    cityMap[p.name] = matchedCities;
});

// // Ê®°ÊãüÊï∞ÊçÆ (ÂÆûÈôÖÂºÄÂèë‰∏≠ÂèØ‰ª•ÂºïÂÖ•Á¨¨‰∏âÊñπÁúÅÂ∏ÇÂå∫Êï∞ÊçÆÂåÖ)
// const DATA = {
//     provinces: ['Âπø‰∏úÁúÅ', 'Âåó‰∫¨Â∏Ç', '‰∏äÊµ∑Â∏Ç', 'ÊµôÊ±üÁúÅ'],
//     cities: {
//         'Âπø‰∏úÁúÅ': ['Áè†Êµ∑Â∏Ç', 'ÂπøÂ∑ûÂ∏Ç', 'Ê∑±Âú≥Â∏Ç', '‰∏úËéûÂ∏Ç'],
//         'Âåó‰∫¨Â∏Ç': ['ÊúùÈò≥Âå∫', 'Êµ∑Ê∑ÄÂå∫', '‰∏úÂüéÂå∫'],
//         '‰∏äÊµ∑Â∏Ç': ['ÈªÑÊµ¶Âå∫', 'ÂæêÊ±áÂå∫', 'Êµ¶‰∏úÊñ∞Âå∫'],
//         'ÊµôÊ±üÁúÅ': ['Êù≠Â∑ûÂ∏Ç', 'ÂÆÅÊ≥¢Â∏Ç', 'Ê∏©Â∑ûÂ∏Ç'],
//     }
// };

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const HomeScreen = ({ navigation }: Props) => {


    // ÊâìÂºÄÈÄâÊã©Âô®
    const openPicker = (type: 'province' | 'city') => {
        setPickingType(type);
        setModalVisible(true);
    };


    const [province, setProvince] = useState('Âπø‰∏úÁúÅ');
    const [city, setCity] = useState('Áè†Êµ∑Â∏Ç');
    const [searchKeyWordValue, setSearchKeyWordValue] = useState('');
    const [modalVisible, setModalVisible] = useState(false);
    const [pickingType, setPickingType] = useState<'province' | 'city'>('province');

    // ‰øÆÊîπÂ§ÑÁêÜÈÄâÊã©ÁöÑÂáΩÊï∞
    const handleSelect = (item: string) => {
        if (pickingType === 'province') {
            setProvince(item);
            // Ëá™Âä®ÈÄâ‰∏≠ËØ•ÁúÅÁöÑÁ¨¨‰∏Ä‰∏™ÂüéÂ∏Ç
            const firstCity = cityMap[item] ? cityMap[item][0] : item;
            setCity(firstCity);
        } else {
            setCity(item);
        }
        setModalVisible(false);
    };




    const handleBannerPress = (hotelId: string) => {
        navigation.navigate('HotelDetail', { hotelId });
    };

    const quickTags = ['Ë±™ÂçéÈÖíÂ∫ó', 'ÂÖçË¥πÂÅúËΩ¶Âú∫', 'Âê´Êó©È§ê', 'ËøëÂú∞ÈìÅ', 'Ê∏©Ê≥âÈÖíÂ∫ó'];

    // 1. Âú®ÁªÑ‰ª∂È°∂ÈÉ®Ê∑ªÂä†Áä∂ÊÄÅ
    const [selectedTags, setSelectedTags] = React.useState<string[]>([]);

    // 2. Ê∑ªÂä†ÁÇπÂáªÂ§ÑÁêÜÂáΩÊï∞
    const toggleTag = (tag: string) => {
        if (selectedTags.includes(tag)) {
            // Â¶ÇÊûúÂ∑≤ÁªèÈÄâ‰∏≠ÔºåÂàôÁßªÈô§ÔºàÂèñÊ∂àÁÇπ‰∫ÆÔºâ
            setSelectedTags(selectedTags.filter(t => t !== tag));
        } else {
            // Â¶ÇÊûúÊ≤°ÈÄâ‰∏≠ÔºåÂàôÊ∑ªÂä†ÔºàÁÇπ‰∫ÆÔºâ
            setSelectedTags([...selectedTags, tag]);
        }
    };

    const handleSearchKeyWordChange = (text: string) => {
        setSearchKeyWordValue(text);
    }

    // --- Êñ∞Â¢ûÔºöÁÇπÂáªÂÆö‰ΩçÊõ¥Êñ∞ÂâçÈù¢ÁöÑÁúÅ‰ªΩÂíåÂ∏ÇÂå∫ ---
    const handleLocationPress = () => {
        // ËøôÈáåÂèØ‰ª•Êé•ÂÖ•ÁúüÂÆûÁöÑÂÆö‰Ωç SDK
        // Ê®°ÊãüÂÆö‰ΩçÂêéÊõ¥Êñ∞‰∏∫Âåó‰∫¨
        setProvince('ÊπñÂåó');
        setCity('Ê≠¶Ê±â');
        Alert.alert('ÂÆö‰ΩçÊàêÂäü', 'Â∑≤Êõ¥Êñ∞Ëá≥ÂΩìÂâç‰ΩçÁΩÆ');
    };

    return (
        <ScrollView style={styles.container} bounces={false}>
            {/* 1. È°∂ÈÉ® Banner Âå∫Âüü (‰øùÁïô) */}
            <TouchableOpacity
                activeOpacity={0.9}
                onPress={() => handleBannerPress('1001')}
                style={styles.bannerContainer}
            >
                <Image
                    source={{ uri: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=800&q=80' }}
                    style={styles.bannerImage}
                />
                <View style={styles.bannerMask}>
                    <Text style={styles.bannerTitle}>Êò•Â≠£ÁâπÊÉ†Ôºö‰∏â‰∫öÊµ∑ÊôØÊàø 5 ÊäòËµ∑xx</Text>
                </View>
            </TouchableOpacity>

            {/* 2. Êü•ËØ¢Âç°ÁâáÂå∫Âüü */}
            <View style={styles.searchCard}>

                {/* --- ‰øÆÊîπÂêéÁöÑÈÉ®ÂàÜÔºöÂ∑¶ËæπÊòæÁ§∫ÁúÅÂ∏Ç‰∏ãÊãâÔºåÂè≥ËæπÁÇπÂáªÂÆö‰Ωç --- */}
                <View style={styles.locationRow}>
                    {/* 1. ÁúÅ‰ªΩ */}
                    <TouchableOpacity style={styles.locationItem} onPress={() => openPicker('province')}>
                        <Text style={styles.label}>ÁúÅ‰ªΩ</Text>
                        <Text style={styles.searchValue}>{province} ‚ñæ</Text>
                    </TouchableOpacity>

                    {/* 2. ÂüéÂ∏Ç */}
                    <TouchableOpacity style={styles.locationItem} onPress={() => openPicker('city')}>
                        <Text style={styles.label}>ÂüéÂ∏Ç</Text>
                        <Text style={styles.searchValue}>{city} ‚ñæ</Text>
                    </TouchableOpacity>

                    {/* 3. ÊàëÁöÑ‰ΩçÁΩÆ */}
                    <TouchableOpacity style={styles.locationBtn} onPress={handleLocationPress}>
                        <Text style={styles.locationIcon}>üìç</Text>
                        <Text style={styles.locationText}>ÊàëÁöÑ‰ΩçÁΩÆ</Text>
                    </TouchableOpacity>
                </View>
                {/* --- ‰øÆÊîπÁªìÊùü --- */}

                {/* --- ÂºπÂá∫ÁöÑÈÄâÊã©Ê°Ü --- */}
                <Modal visible={modalVisible} animationType="slide" transparent={true}>
                    <View style={styles.modalOverlay}>
                        <View style={styles.modalContent}>
                            <View style={styles.modalHeader}>
                                <Text style={styles.modalTitle}>ËØ∑ÈÄâÊã©{pickingType === 'province' ? 'ÁúÅ‰ªΩ' : 'ÂüéÂ∏Ç'}</Text>
                                <TouchableOpacity onPress={() => setModalVisible(false)}>
                                    <Text style={{ color: '#999' }}>ÂèñÊ∂à</Text>
                                </TouchableOpacity>
                            </View>
                            <FlatList
                                data={pickingType === 'province' ? DATA.provinces : DATA.cities[province as keyof typeof DATA.cities]}
                                keyExtractor={(item) => item}
                                renderItem={({ item }) => (
                                    <TouchableOpacity
                                        style={styles.modalItem}
                                        onPress={() => handleSelect(item)}
                                    >
                                        <Text style={[
                                            styles.modalItemText,
                                            (pickingType === 'province' ? province === item : city === item) && { color: '#007AFF', fontWeight: 'bold' }
                                        ]}>{item}</Text>
                                    </TouchableOpacity>
                                )}
                            />
                        </View>
                    </View>
                </Modal>

                <View style={styles.divider} />

                {/* b. ÂÖ≥ÈîÆÂ≠óÊêúÁ¥¢ (‰øùÁïô) */}
                <View style={styles.searchItem}>
                    <Text style={styles.label}>‰ΩçÁΩÆ/ÂìÅÁâå/ÈÖíÂ∫ó</Text>
                    <TextInput
                        placeholder="ÊêúÁ¥¢‰Ω†ÊÉ≥ÂéªÁöÑÂú∞ÁÇπ"
                        placeholderTextColor="#ccc"
                        style={styles.searchInput}
                        value={searchKeyWordValue}
                        onChangeText={handleSearchKeyWordChange}
                    />
                </View>

                <View style={styles.divider} />

                {/* c. ÂÖ•‰ΩèÊó•ÊúüÈÄâÊã© (‰øùÁïô) */}
                <TouchableOpacity style={styles.dateRow}>
                    <View style={{ flex: 1 }}>
                        <Text style={styles.label}>ÂÖ•‰Ωè</Text>
                        <Text style={styles.dateText}>05Êúà01Êó•xx <Text style={styles.weekText}>Âë®‰∏â</Text></Text>
                    </View>
                    <View style={styles.nightBadge}>
                        <Text style={styles.nightText}>2Êôöxx</Text>
                    </View>
                    <View style={{ flex: 1, alignItems: 'flex-end' }}>
                        <Text style={styles.label}>Á¶ªÂ∫ó</Text>
                        <Text style={styles.dateText}>05Êúà03Êó•xx <Text style={styles.weekText}>Âë®‰∫î</Text></Text>
                    </View>
                </TouchableOpacity>

                <View style={styles.divider} />

                {/* d. Á≠õÈÄâÊù°‰ª∂ (‰øùÁïô) */}
                {/* <TouchableOpacity style={styles.searchItem}>
                    <Text style={styles.label}>‰ª∑Ê†º/ÊòüÁ∫ß</Text>
                    <Text style={styles.searchValue}>, </Text>
                </TouchableOpacity> */}

                <View style={styles.locationRow}>
                    <TouchableOpacity style={styles.locationItem} onPress={() => { }}>
                        <Text style={styles.label}>‰ª∑Ê†º</Text>
                        <Text style={styles.searchValue}>Ôø•200-500 ‚ñæ</Text>
                    </TouchableOpacity>

