import { Dimensions, StyleSheet } from 'react-native';

const { width } = Dimensions.get('window');

export const styles = StyleSheet.create({
    // 搜索项的横向容器
    inputRow: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 10,
    },
    // 左侧城市选择区
    citySelector: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingRight: 12,
        borderRightWidth: StyleSheet.hairlineWidth,
        borderRightColor: '#eee',
        minWidth: 70,
    },
    cityText: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#333',
    },
    dropdownIcon: {
        fontSize: 10,
        color: '#999',
        marginLeft: 4,
        marginTop: 4,
    },
    // 中间输入框区
    inputContainer: {
        flex: 1,
        paddingHorizontal: 12,
    },
    searchInput: {
        fontSize: 15,
        color: '#333',
        padding: 0, // 去除安卓默认内边距
    },
    // 右侧定位区
    locationBtn: {
        alignItems: 'center',
        justifyContent: 'center',
        paddingLeft: 12,
    },
    locationIcon: {
        fontSize: 18,
        marginBottom: 2,
    },
    locationText: {
        fontSize: 10,
        color: '#007AFF',
    },
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    bannerContainer: {
        width: width,
        height: 180,
    },
    bannerImage: {
        width: '100%',
        height: '100%',
    },
    bannerMask: {
        position: 'absolute',
        bottom: 0,
        width: '100%',
        padding: 15,
        backgroundColor: 'rgba(0,0,0,0.3)',
    },
    bannerTitle: {
        color: '#fff',
        fontSize: 18,
        fontWeight: 'bold',
    },
    searchCard: {
        margin: 15,
        marginTop: -10,
        backgroundColor: '#fff',
        borderRadius: 12,
        padding: 16,
        elevation: 4,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 10,
    },
    locationRow: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 10,
    },
    
    dateText: { fontSize: 18, fontWeight: '600', color: '#333', marginTop: 4 },
    weekText: { fontSize: 12, fontWeight: 'normal', color: '#999' },
    nightBadge: {
        borderWidth: 1,
        borderColor: '#eee',
        borderRadius: 10,
        paddingHorizontal: 8,
        height: 20,
        justifyContent: 'center',
    },
    nightText: { fontSize: 10, color: '#666' },
    tagScroll: {
        marginHorizontal: -16,
        paddingHorizontal: 16,
        marginTop: 15,
    },
    tagItem: {
        backgroundColor: '#f2f2f2',
        paddingHorizontal: 12,
        paddingVertical: 6,
        borderRadius: 4,
        marginRight: 8,
    },
    tagText: { fontSize: 12, color: '#666' },
    searchItem: { paddingVertical: 12 },
    label: { fontSize: 12, color: '#999' },
    searchValue: { fontSize: 16, fontWeight: '600', color: '#333', marginTop: 4 },
    divider: { height: 1, backgroundColor: '#f0f0f0' },
    searchButton: {
        backgroundColor: '#007AFF',
        borderRadius: 8,
        height: 48,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 20,
    },
    searchButtonText: { color: '#fff', fontSize: 18, fontWeight: 'bold' },
    infoSection: { alignItems: 'center', marginVertical: 20 },
    infoText: { color: '#ccc', fontSize: 12 }
});
