import { Button, Carousel, Provider, Toast } from '@ant-design/react-native';
import { Ionicons } from '@expo/vector-icons'; // 使用 Expo 内置图标
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import dayjs from 'dayjs';
import React, { useMemo, useState } from 'react';
import {
    Dimensions,
    ImageBackground,
    ScrollView,
    StatusBar,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const { width } = Dimensions.get('window');

// 模拟 Banner 数据
const BANNERS = [
    'https://images.unsplash.com/photo-1571003123894-1f0594d2b5d9?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
    'https://images.unsplash.com/photo-1566073771259-6a8506099945?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
];

// 快捷标签数据
const QUICK_TAGS = ['免费停车场', '近地铁', '京西古道', '环球影城', '迪士尼', '温泉酒店'];

const HomeScreen = ({ navigation }: Props) => {
    // --- 1. 状态管理 ---
    const [city, setCity] = useState('上海');
    const [keyword, setKeyword] = useState('');
    // 默认入住今天，离店明天
    const [dateRange, setDateRange] = useState({
        start: dayjs(),
        end: dayjs().add(1, 'day')
    });

    // --- 2. 计算逻辑 ---
    const nights = useMemo(() => {
        return dateRange.end.diff(dateRange.start, 'day');
    }, [dateRange]);

    const formatDate = (date: dayjs.Dayjs) => date.format('MM月DD日');
    const getWeekDay = (date: dayjs.Dayjs) => {
        const days = ['周日', '周一', '周二', '周三', '周四', '周五', '周六'];
        return date.isSame(dayjs(), 'day') ? '今天' :
            date.isSame(dayjs().add(1, 'day'), 'day') ? '明天' :
                days[date.day()];
    };

    // --- 3. 交互事件 ---
    const handleLocation = () => {
        Toast.loading('正在定位...', 1, () => {
            setCity('北京'); // 模拟定位结果
            Toast.success('已定位到当前城市', 1);
        });
    };

    const handleSearch = () => {
        // 跳转到列表页，并携带所有查询参数
        navigation.navigate('HotelList', {
            city,
            startDate: dateRange.start.format('YYYY-MM-DD'),
            endDate: dateRange.end.format('YYYY-MM-DD'),
            keyword
        });
    };

    // --- 4. 渲染 UI ---
    return (
        <Provider>
            <View style={styles.container}>
                <StatusBar barStyle="light-content" backgroundColor="transparent" />

                <ScrollView contentContainerStyle={{ paddingBottom: 40 }} showsVerticalScrollIndicator={false}>

                    {/* A. 顶部 Banner (沉浸式背景) */}
                    <View style={styles.bannerContainer}>
                        <Carousel
                            style={styles.wrapper}
                            selectedIndex={0}
                            autoplay
                            infinite
                            dotStyle={{ backgroundColor: 'rgba(255,255,255,0.4)' }}
                            dotActiveStyle={{ backgroundColor: '#fff' }}
                        >
                            {BANNERS.map((url, index) => (
                                <ImageBackground
                                    key={index}
                                    source={{ uri: url }}
                                    style={styles.bannerImage}
                                    resizeMode="cover"
                                >
                                    {/* 模拟顶部的 slogan */}
                                    <View style={styles.sloganContainer}>
                                        <Text style={styles.sloganTitle}>酒店 5 折起</Text>
                                        <Text style={styles.sloganSub}>特价房源 限时抢购</Text>
                                    </View>
                                </ImageBackground>
                            ))}
                        </Carousel>
                    </View>

                    {/* B. 核心查询区域 (悬浮卡片) */}
                    <View style={styles.searchCard}>

                        {/* 顶部 Tab (装饰性) */}
                        <View style={styles.tabRow}>
                            <Text style={[styles.tabText, styles.activeTab]}>国内</Text>
                            <Text style={styles.tabText}>海外</Text>
                            <Text style={styles.tabText}>钟点房</Text>
                            <Text style={styles.tabText}>民宿</Text>
                        </View>

                        {/* a. 城市与定位 */}
                        <View style={[styles.inputRow, { marginTop: 15 }]}>
                            <TouchableOpacity style={styles.citySelector}>
                                <Text style={styles.cityText}>{city}</Text>
                                <Ionicons name="caret-down" size={12} color="#333" />
                            </TouchableOpacity>

                            <TextInput
                                style={styles.searchInput}
                                placeholder="我的位置/品牌/酒店"
                                value={keyword}
                                onChangeText={setKeyword}
                            />

                            <TouchableOpacity onPress={handleLocation} style={styles.locationBtn}>
                                <Ionicons name="locate" size={20} color="#007AFF" />
                                <Text style={styles.locationLabel}>我的位置</Text>
                            </TouchableOpacity>
                        </View>

                        <View style={styles.divider} />

                        {/* c. 入住日期选择 */}
                        <TouchableOpacity style={styles.dateRow} activeOpacity={0.7}>
                            <View style={styles.dateItem}>
                                <Text style={styles.dateLabel}>入住</Text>
                                <View style={styles.dateValueRow}>
                                    <Text style={styles.dateValue}>{formatDate(dateRange.start)}</Text>
                                    <Text style={styles.weekDay}>{getWeekDay(dateRange.start)}</Text>
                                </View>
                            </View>

                            <View style={styles.nightCount}>
                                <Text style={styles.nightText}>共{nights}晚</Text>
                            </View>

                            <View style={styles.dateItem}>
                                <Text style={styles.dateLabel}>离店</Text>
                                <View style={styles.dateValueRow}>
                                    <Text style={styles.dateValue}>{formatDate(dateRange.end)}</Text>
                                    <Text style={styles.weekDay}>{getWeekDay(dateRange.end)}</Text>
                                </View>
                            </View>
                        </TouchableOpacity>

                        <View style={styles.divider} />

                        {/* d. 筛选条件 (星级/价格) */}
                        <TouchableOpacity style={styles.filterRow}>
                            <Text style={styles.placeholderText}>价格/星级</Text>
                            <Ionicons name="chevron-forward" size={16} color="#ccc" />
                        </TouchableOpacity>

                        <View style={styles.divider} />

                        {/* e. 快捷标签 (横向滚动) */}
                        <View style={styles.tagsContainer}>
                            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
                                {QUICK_TAGS.map((tag, index) => (
                                    <TouchableOpacity key={index} style={styles.tagItem}>
                                        <Text style={styles.tagTextItem}>{tag}</Text>
                                    </TouchableOpacity>
                                ))}
                            </ScrollView>
                        </View>

                        {/* 查询按钮 */}
                        <Button type="primary" style={styles.searchBtn} onPress={handleSearch}>
                            <Text style={styles.searchBtnText}>查询</Text>
                        </Button>

                    </View>

                </ScrollView>
            </View>
        </Provider>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, backgroundColor: '#f2f4f6' },

    // Banner 样式
    bannerContainer: { height: 260, width: '100%' },
    wrapper: { height: 260 },
    bannerImage: { width: '100%', height: '100%', justifyContent: 'center', paddingLeft: 20 },
    sloganContainer: { marginTop: -40 },
    sloganTitle: { color: '#fff', fontSize: 28, fontWeight: '800', textShadowColor: 'rgba(0,0,0,0.3)', textShadowRadius: 10 },
    sloganSub: { color: '#fff', fontSize: 16, marginTop: 5, fontWeight: '500' },

    // 悬浮卡片样式
    searchCard: {
        marginHorizontal: 12,
        marginTop: -50, // 关键：负边距实现悬浮效果
        backgroundColor: '#fff',
        borderRadius: 16,
        padding: 16,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.08,
        shadowRadius: 10,
        elevation: 5,
    },

    // Tab 样式
    tabRow: { flexDirection: 'row', alignItems: 'baseline', marginBottom: 10 },
    tabText: { fontSize: 16, color: '#666', marginRight: 20, fontWeight: '500' },
    activeTab: { fontSize: 20, color: '#007AFF', fontWeight: 'bold' },

    // 输入行通用样式
    inputRow: { flexDirection: 'row', alignItems: 'center', height: 50 },
    divider: { height: 1, backgroundColor: '#f0f0f0', marginVertical: 8 },

    // 城市与定位
    citySelector: { flexDirection: 'row', alignItems: 'center', marginRight: 15 },
    cityText: { fontSize: 18, fontWeight: 'bold', marginRight: 4 },
    searchInput: { flex: 1, fontSize: 16, height: '100%' },
    locationBtn: { alignItems: 'center', justifyContent: 'center', paddingLeft: 10 },
    locationLabel: { fontSize: 10, color: '#007AFF', marginTop: 2 },

    // 日期选择
    dateRow: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', paddingVertical: 8 },
    dateItem: { flex: 1 },
    dateLabel: { fontSize: 12, color: '#999', marginBottom: 4 },
    dateValueRow: { flexDirection: 'row', alignItems: 'flex-end' },
    dateValue: { fontSize: 20, fontWeight: 'bold', color: '#333', marginRight: 6 },
    weekDay: { fontSize: 12, color: '#333', marginBottom: 3 },
    nightCount: { paddingHorizontal: 8, paddingVertical: 2, borderWidth: 0.5, borderColor: '#eee', borderRadius: 10 },
    nightText: { fontSize: 11, color: '#666' },

    // 筛选与标签
    filterRow: { height: 44, justifyContent: 'center' },
    placeholderText: { fontSize: 16, color: '#ccc' },

    tagsContainer: { flexDirection: 'row', marginVertical: 12 },
    tagItem: { backgroundColor: '#f5f7fa', paddingHorizontal: 10, paddingVertical: 6, borderRadius: 4, marginRight: 8 },
    tagTextItem: { color: '#666', fontSize: 12 },

    // 按钮
    searchBtn: { backgroundColor: '#007AFF', borderRadius: 25, height: 50, borderWidth: 0 },
    searchBtnText: { fontSize: 18, fontWeight: 'bold' }
});

export default HomeScreen;
