import Geolocation from '@react-native-community/geolocation';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import { Alert, PermissionsAndroid, Platform } from 'react-native';

import React, { useState } from 'react';
// ÂØºÂÖ•ÁúÅÁ∫ßÂíåÂú∞Á∫ßÊï∞ÊçÆ
import citiesRaw from 'china-division/dist/cities.json';
import provincesRaw from 'china-division/dist/provinces.json';
import {
    FlatList,
    Image,
    Modal,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';
import { styles } from './index.styles';


// ÊèêÂèñÁúÅ‰ªΩÂêçÁß∞ÂàóË°®
const provinceList = provincesRaw.map(p => p.name);
// ÂÆö‰πâÊï∞ÊçÆ
const FILTER_DATA = {
    prices: ['‰∏çÈôê', 'Ôø•0-150', 'Ôø•150-300', 'Ôø•300-600', 'Ôø•600-1000', 'Ôø•1000‰ª•‰∏ä'],
    roomTypes: ['‰∏çÈôê', 'Â§ßÂ∫äÊàø', 'Âèå‰∫∫Âª∫Á≠ë', 'Âçï‰∫∫Â∫ä', '‰∏â‰∫∫Èó¥', 'Â•óÊàø']
};

// ÊèêÂèñÂüéÂ∏ÇÊò†Â∞ÑÔºö { "Âπø‰∏úÁúÅ": ["ÂπøÂ∑ûÂ∏Ç", "Ê∑±Âú≥Â∏Ç", ...], "Âåó‰∫¨Â∏Ç": ["Âåó‰∫¨Â∏Ç"] }
const cityMap: Record<string, string[]> = {};
provincesRaw.forEach(p => {
    // ËøáÊª§Âá∫Â±û‰∫éËØ•ÁúÅÁöÑÊâÄÊúâÂüéÂ∏Ç
    const matchedCities = citiesRaw
        .filter(c => c.provinceCode === p.code)
        .map(c => c.name);
    cityMap[p.name] = matchedCities;
});


type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const HomeScreen = ({ navigation }: Props) => {
    const quickTags = ['Ë±™ÂçéÈÖíÂ∫ó', 'ÂÖçË¥πÂÅúËΩ¶Âú∫', 'Âê´Êó©È§ê', 'ËøëÂú∞ÈìÅ', 'Ê∏©Ê≥âÈÖíÂ∫ó'];

    // 2. Êñ∞Â¢û‰ª∑Ê†ºÂíåÊàøÂûãÁöÑÁä∂ÊÄÅ
    const [price, setPrice] = useState('Ôø•200-500');
    const [roomType, setRoomType] = useState('Â§ßÂ∫äÊàø');
    const [province, setProvince] = useState('Âπø‰∏úÁúÅ');
    const [city, setCity] = useState('Áè†Êµ∑Â∏Ç');
    const [searchKeyWordValue, setSearchKeyWordValue] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    // 3. Êâ©Â±ï pickingType ÁöÑÁ±ªÂûã
    const [modalVisible, setModalVisible] = useState(false);
    const [pickingType, setPickingType] = useState<'province' | 'city' | 'price' | 'roomType'>('province');

    // 4. Áªü‰∏ÄÁöÑÊâìÂºÄÂºπÁ™óÂáΩÊï∞
    const openPicker = (type: typeof pickingType) => {
        setPickingType(type);
        setModalVisible(true);
    };


    // 5. Áªü‰∏ÄÁöÑÈÄâÊã©Â§ÑÁêÜÈÄªËæë
    const handleSelect = (item: string) => {
        switch (pickingType) {
            case 'province':
                setProvince(item);
                setCity(cityMap[item][0]); // ËÅîÂä®ÈáçÁΩÆÂüéÂ∏Ç
                break;
            case 'city':
                setCity(item);
                break;
            case 'price':
                setPrice(item);
                break;
            case 'roomType':
                setRoomType(item);
                break;
        }
        setModalVisible(false);
    };

    // 6. Âä®ÊÄÅËé∑ÂèñÂΩìÂâçÂºπÁ™óÂ∫îËØ•ÊòæÁ§∫ÁöÑÂàóË°®Êï∞ÊçÆ
    const getModalData = () => {
        switch (pickingType) {
            case 'province': return provinceList;
            case 'city': return cityMap[province] || [];
            case 'price': return FILTER_DATA.prices;
            case 'roomType': return FILTER_DATA.roomTypes;
            default: return [];
        }
    };


    const handleLocationPress = async () => {
        setIsLoading(true);

        // 1. Áî≥ËØ∑ÊùÉÈôê (Android ÂøÖÈ°ªÊâãÂä®Áî≥ËØ∑)
        if (Platform.OS === 'android') {
            const granted = await PermissionsAndroid.request(
                PermissionsAndroid.PERMISSIONS.ACCESS_FINE_LOCATION
            );
            if (granted !== PermissionsAndroid.RESULTS.GRANTED) {
                Alert.alert("ÊùÉÈôêË¢´ÊãíÁªù", "ËØ∑Âú®ËÆæÁΩÆ‰∏≠ÂºÄÂêØÂÆö‰ΩçÊùÉÈôê");
                setIsLoading(false);
                return;
            }
        }

        // 2. ‰ΩøÁî®ÂéüÁîü Geolocation Ëé∑ÂèñÁªèÁ∫¨Â∫¶
        Geolocation.getCurrentPosition(
            async (position) => {
                const { latitude, longitude } = position.coords;

                // 3. Ë∞ÉÁî®ÈÄÜÂú∞ÁêÜÁºñÁ†ÅÊé•Âè£ (ËøôÈáå‰ª•È´òÂæ∑ HTTP API ‰∏∫‰æãÔºå‰∏çÈúÄË¶Å SDK)
                // ÂéªÈ´òÂæ∑ÂºÄÊîæÂπ≥Âè∞Áî≥ËØ∑‰∏Ä‰∏™ WebÊúçÂä°Á±ªÂûãÁöÑ Key (ÂÖçË¥π)
                // const AMAP_KEY = '‰Ω†Áî≥ËØ∑ÁöÑWebÊúçÂä°Key';
                const AMAP_KEY = '‰Ω†Áî≥ËØ∑ÁöÑWebÊúçÂä°Key';

                const url = `https://restapi.amap.com/v3/geocode/regeo?output=json&location=${longitude},${latitude}&key=${AMAP_KEY}`;

                try {
                    const response = await fetch(url);
                    const result = await response.json();

                    if (result.status === '1') {
                        const addressComponent = result.regeocode.addressComponent;
                        // Êõ¥Êñ∞Áä∂ÊÄÅ
                        setProvince(addressComponent.province);
                        // Â§ÑÁêÜÁõ¥ËæñÂ∏ÇÔºöÂ¶ÇÊûúÊòØÂåó‰∫¨Ôºåcity ÂèØËÉΩÊòØÁ©∫Êï∞ÁªÑÊàñÂ≠óÁ¨¶‰∏≤ÔºåÈúÄË¶ÅÂÅöÂÖºÂÆπ
                        const cityName = typeof addressComponent.city === 'string'
                            ? addressComponent.city
                            : addressComponent.province;
                        setCity(cityName);
                    } else {
                        Alert.alert("Ëß£ÊûêÂ§±Ë¥•", "Êó†Ê≥ïËé∑ÂèñÂÖ∑‰ΩìÂüéÂ∏ÇÂêç");
                    }
                } catch (error) {
                    Alert.alert("ÁΩëÁªúÈîôËØØ", "Ëé∑Âèñ‰ΩçÁΩÆÂêçÁß∞Â§±Ë¥•");
                } finally {
                    setIsLoading(false);
                }
            },
            (error) => {
                Alert.alert("ÂÆö‰ΩçÂ§±Ë¥•", error.message);
                setIsLoading(false);
            },
            { enableHighAccuracy: false, timeout: 20000, maximumAge: 1000 }
        );
    };

    const handleBannerPress = (hotelId: string) => {
        navigation.navigate('HotelDetail', { hotelId });
    };



    // 1. Âú®ÁªÑ‰ª∂È°∂ÈÉ®Ê∑ªÂä†Áä∂ÊÄÅ
