import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useState } from 'react';
import {
    Alert,
    Image,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';
import { styles } from './index.styles';

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const HomeScreen = ({ navigation }: Props) => {
    // 1. ÂÆö‰πâÁúÅ‰ªΩÂíåÂüéÂ∏ÇÁöÑÁä∂ÊÄÅÔºåÈªòËÆ§‰∏∫Âπø‰∏úÁúÅ Áè†Êµ∑Â∏Ç
    const [province, setProvince] = useState('Âπø‰∏úÁúÅ');
    const [city, setCity] = useState('Áè†Êµ∑Â∏Ç');
    const [searchKeyWordValue, setSearchKeyWordValue] = useState('');

    const handleBannerPress = (hotelId: string) => {
        navigation.navigate('HotelDetail', { hotelId });
    };

    const quickTags = ['Ë±™ÂçéÈÖíÂ∫ó', 'ÂÖçË¥πÂÅúËΩ¶Âú∫', 'Âê´Êó©È§ê', 'ËøëÂú∞ÈìÅ', 'Ê∏©Ê≥âÈÖíÂ∫ó'];

    // 2. Ê®°ÊãüÁÇπÂáªÂÆö‰ΩçÊõ¥Êñ∞‰ΩçÁΩÆ
    const handleLocationPress = () => {
        // ËøôÈáåÂÆûÈôÖÂºÄÂèë‰∏≠‰ºöË∞ÉÁî® Geolocation API
        // Ê®°ÊãüÂÆö‰ΩçËé∑ÂèñÂà∞‰∫ÜÊñ∞ÁöÑ‰ΩçÁΩÆÔºö‰∏äÊµ∑Â∏Ç
        setProvince('‰∏äÊµ∑Â∏Ç');
        setCity('‰∏äÊµ∑Â∏Ç');
        Alert.alert('ÂÆö‰ΩçÊàêÂäü', 'Â∑≤Êõ¥Êñ∞Ëá≥ÂΩìÂâç‰ΩçÁΩÆ');
    };

    return (
        <ScrollView style={styles.container} bounces={false}>
            {/* 1. È°∂ÈÉ® Banner Âå∫Âüü */}
            <TouchableOpacity
                activeOpacity={0.9}
                onPress={() => handleBannerPress('1001')}
                style={styles.bannerContainer}
            >
                <Image
                    source={{ uri: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=800&q=80' }}
                    style={styles.bannerImage}
                />
                <View style={styles.bannerMask}>
                    <Text style={styles.bannerTitle}>Êò•Â≠£ÁâπÊÉ†Ôºö‰∏â‰∫öÊµ∑ÊôØÊàø 5 ÊäòËµ∑</Text>
                </View>
            </TouchableOpacity>

            {/* 2. Êü•ËØ¢Âç°ÁâáÂå∫Âüü */}
            <View style={styles.searchCard}>
                {/* a. ÁõÆÁöÑÂú∞ÔºàÁúÅ„ÄÅÂ∏ÇÔºâ‰∏éÂÆö‰Ωç */}
                <View style={styles.locationRow}>
                    {/* Â∑¶‰æßÈÄâÊã©Âå∫Âüü */}
                    <View style={{ flex: 1, flexDirection: 'row', alignItems: 'center' }}>
                        <TouchableOpacity
                            onPress={() => console.log('ÈÄâÊã©ÁúÅ‰ªΩ')}
                            style={{ marginRight: 10 }}
                        >
                            <Text style={styles.searchValue}>{province}</Text>
                            <Text style={{ fontSize: 10, color: '#999' }}>ÁúÅ‰ªΩ ‚ñº</Text>
                        </TouchableOpacity>

                        <TouchableOpacity
                            onPress={() => console.log('ÈÄâÊã©ÂüéÂ∏Ç')}
                            style={{ flex: 1 }}
                        >
                            <Text style={styles.searchValue}>{city}</Text>
                            <Text style={{ fontSize: 10, color: '#999' }}>ÂüéÂ∏Ç ‚ñº</Text>
                        </TouchableOpacity>
                    </View>

                    {/* Âè≥‰æßÂÆö‰ΩçÊåâÈíÆ */}
                    <TouchableOpacity
                        style={styles.locationBtn}
                        onPress={handleLocationPress}
                    >
                        <Text style={styles.locationIcon}>üìç</Text>
                        <Text style={styles.locationText}>ÊàëÁöÑ‰ΩçÁΩÆ</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.divider} />

                {/* b. ÂÖ≥ÈîÆÂ≠óÊêúÁ¥¢ */}
                <View style={styles.searchItem}>
                    <Text style={styles.label}>ÂÖ≥ÈîÆËØç/ÈÖíÂ∫ó/Âú∞ÂùÄ</Text>
                    <TextInput
                        placeholder="ÊêúÁ¥¢‰Ω†ÊÉ≥ÂéªÁöÑÂú∞ÁÇπ"
                        placeholderTextColor="#ccc"
                        style={styles.searchInput}
                        value={searchKeyWordValue}
                        onChangeText={setSearchKeyWordValue}
                    />
                </View>

                <View style={styles.divider} />

                {/* c. ÂÖ•‰ΩèÊó•ÊúüÈÄâÊã© (Áï•Ôºå‰øùÊåÅ‰Ω†ÂéüÊúâÁöÑ) */}
                {/* ... */}

                {/* Á´ãÂç≥Êü•ËØ¢ÊåâÈíÆ - ‰º†ÂÖ•ÊúÄÊñ∞ÁöÑÁúÅÂ∏ÇÁä∂ÊÄÅ */}
                <TouchableOpacity
                    style={styles.searchButton}
                    onPress={() => navigation.navigate('HotelList', {
                        city: city,
                        // province: province,
                        startDate: '2024-05-01',
                        endDate: '2024-05-03'
                    })}
                >
                    <Text style={styles.searchButtonText}>Á´ãÂç≥Êü•ËØ¢</Text>
                </TouchableOpacity>
            </View>

            <View style={styles.infoSection}>
                <Text style={styles.infoText}>¬∑ ÂÆòÊñπÁõ¥Ëê• ¬∑ ÊûÅÈÄüÁ°ÆËÆ§ ¬∑ Êó†ÂøßÈÄÄÊîπ</Text>
            </View>
        </ScrollView>
    );
};

export default HomeScreen;
