import Geolocation from '@react-native-community/geolocation';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import { Alert, PermissionsAndroid, Platform } from 'react-native';


import React, { useEffect, useRef, useState } from 'react';
// ÂØºÂÖ•ÁúÅÁ∫ßÂíåÂú∞Á∫ßÊï∞ÊçÆ
import citiesRaw from 'china-division/dist/cities.json';
import provincesRaw from 'china-division/dist/provinces.json';
import {
    FlatList,
    Image,
    Modal,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';
import { styles, width } from './index.styles';


// ÊèêÂèñÁúÅ‰ªΩÂêçÁß∞ÂàóË°®
const provinceList = provincesRaw.map(p => p.name);
// ÂÆö‰πâÊï∞ÊçÆ
const FILTER_DATA = {
    prices: ['‰∏çÈôê', 'Ôø•0-150', 'Ôø•150-300', 'Ôø•300-600', 'Ôø•600-1000', 'Ôø•1000‰ª•‰∏ä'],
    roomTypes: ['‰∏çÈôê', 'Â§ßÂ∫äÊàø', 'Âèå‰∫∫Âª∫Á≠ë', 'Âçï‰∫∫Â∫ä', '‰∏â‰∫∫Èó¥', 'Â•óÊàø']
};

// ÂÆö‰πâËΩÆÊí≠ÂõæÊï∞ÊçÆ
const BANNERS = [
    { id: '1', title: 'Êò•Â≠£ÁâπÊÉ†Ôºö‰∏â‰∫öÊµ∑ÊôØÊàø 5 ÊäòËµ∑', uri: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=800' },
    { id: '2', title: 'Ê∑±Â±±ÈÅøÊöëÔºöËé´Âπ≤Â±±Á≤æÂìÅÊ∞ëÂÆø', uri: 'https://images.unsplash.com/photo-1582719478250-c89cae4dc85b?w=800' },
    { id: '3', title: 'È≠îÈÉΩ‰πãÂ§úÔºö‰∏äÊµ∑Â§ñÊª©ÊôØËßÇÊàø', uri: 'https://images.unsplash.com/photo-1506059612708-99d6c258160e?w=800' },
    { id: '4', title: 'Âè§ÂüéÈüµÂë≥ÔºöÂ§ßÁêÜÊ¥±Êµ∑Èò≥ÂÖâÊàø', uri: 'https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?w=800' },
    { id: '5', title: '‰∫≤Â≠êÊó∂ÂÖâÔºöÈïøÈöÜ‰∏ªÈ¢òÈÖíÂ∫ó', uri: 'https://images.unsplash.com/photo-1520250497591-112f2f40a3f4?w=800' },
];

// ÊèêÂèñÂüéÂ∏ÇÊò†Â∞ÑÔºö { "Âπø‰∏úÁúÅ": ["ÂπøÂ∑ûÂ∏Ç", "Ê∑±Âú≥Â∏Ç", ...], "Âåó‰∫¨Â∏Ç": ["Âåó‰∫¨Â∏Ç"] }
const cityMap: Record<string, string[]> = {};
provincesRaw.forEach(p => {
    // ËøáÊª§Âá∫Â±û‰∫éËØ•ÁúÅÁöÑÊâÄÊúâÂüéÂ∏Ç
    const matchedCities = citiesRaw
        .filter(c => c.provinceCode === p.code)
        .map(c => c.name);
    cityMap[p.name] = matchedCities;
});


type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const HomeScreen = ({ navigation }: Props) => {
    const quickTags = ['Ë±™ÂçéÈÖíÂ∫ó', 'ÂÖçË¥πÂÅúËΩ¶Âú∫', 'Âê´Êó©È§ê', 'ËøëÂú∞ÈìÅ', 'Ê∏©Ê≥âÈÖíÂ∫ó'];

    const scrollRef = useRef<ScrollView>(null); // 1. ÂàõÂª∫ ScrollView ÁöÑÂºïÁî®

    // 2. Êñ∞Â¢û‰ª∑Ê†ºÂíåÊàøÂûãÁöÑÁä∂ÊÄÅ
    const [price, setPrice] = useState('Ôø•200-500');
    const [roomType, setRoomType] = useState('Â§ßÂ∫äÊàø');
    const [province, setProvince] = useState('Âπø‰∏úÁúÅ');
    const [city, setCity] = useState('Áè†Êµ∑Â∏Ç');
    const [searchKeyWordValue, setSearchKeyWordValue] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    //ËÆ∞ÂΩïÂΩìÂâçÊªöÂä®Âà∞‰∫ÜÁ¨¨Âá†Âº†ÔºàÁî®‰∫éÊòæÁ§∫ÊåáÁ§∫ÁÇπÔºâ
    const [activeIndex, setActiveIndex] = React.useState(0);

    // 3. Êâ©Â±ï pickingType ÁöÑÁ±ªÂûã
    const [modalVisible, setModalVisible] = useState(false);
    const [pickingType, setPickingType] = useState<'province' | 'city' | 'price' | 'roomType'>('province');

    // 2. Ëá™Âä®Êí≠ÊîæÈÄªËæë
    useEffect(() => {
        const timer = setInterval(() => {
            let nextIndex = activeIndex + 1;
            if (nextIndex >= BANNERS.length) {
                nextIndex = 0; // Â¶ÇÊûúÊòØÊúÄÂêé‰∏ÄÂº†ÔºåÂõûÂà∞Á¨¨‰∏ÄÂº†
            }

            // 3. ËÆ© ScrollView ÊªöÂä®Âà∞ËÆ°ÁÆóÂá∫ÁöÑ‰ΩçÁΩÆ
            scrollRef.current?.scrollTo({
                x: nextIndex * width,
                animated: true,
            });

            setActiveIndex(nextIndex);
        }, 2000); // 2000ÊØ´Áßí = 2Áßí

        return () => clearInterval(timer); // ÁªÑ‰ª∂ÈîÄÊØÅÊó∂Ê∏ÖÈô§ÂÆöÊó∂Âô®ÔºåÈò≤Ê≠¢ÂÜÖÂ≠òÊ≥ÑÊºè
    }, [activeIndex]); // ÁõëÂê¨ index ÂèòÂåñ

    // ÊâãÂä®ÊªëÂä®Êó∂ÁöÑÂõûË∞É
    const handleScroll = (event: any) => {
        const x = event.nativeEvent.contentOffset.x;
        const index = Math.round(x / width);
        if (index !== activeIndex) {
            setActiveIndex(index);
        }
    };


    // 4. Áªü‰∏ÄÁöÑÊâìÂºÄÂºπÁ™óÂáΩÊï∞
    const openPicker = (type: typeof pickingType) => {
        setPickingType(type);
        setModalVisible(true);
    };


    // 5. Áªü‰∏ÄÁöÑÈÄâÊã©Â§ÑÁêÜÈÄªËæë
    const handleSelect = (item: string) => {
        switch (pickingType) {
            case 'province':
                setProvince(item);
                setCity(cityMap[item][0]); // ËÅîÂä®ÈáçÁΩÆÂüéÂ∏Ç
                break;
            case 'city':
                setCity(item);
                break;
            case 'price':
                setPrice(item);
                break;
            case 'roomType':
                setRoomType(item);
                break;
        }
        setModalVisible(false);
    };

    // 6. Âä®ÊÄÅËé∑ÂèñÂΩìÂâçÂºπÁ™óÂ∫îËØ•ÊòæÁ§∫ÁöÑÂàóË°®Êï∞ÊçÆ
    const getModalData = () => {
        switch (pickingType) {
            case 'province': return provinceList;
            case 'city': return cityMap[province] || [];
            case 'price': return FILTER_DATA.prices;
            case 'roomType': return FILTER_DATA.roomTypes;
            default: return [];
        }
    };


    const handleLocationPress = async () => {
        setIsLoading(true);

        // 1. Áî≥ËØ∑ÊùÉÈôê (Android ÂøÖÈ°ªÊâãÂä®Áî≥ËØ∑)
        if (Platform.OS === 'android') {
            const granted = await PermissionsAndroid.request(
                PermissionsAndroid.PERMISSIONS.ACCESS_FINE_LOCATION
            );
            if (granted !== PermissionsAndroid.RESULTS.GRANTED) {
                Alert.alert("ÊùÉÈôêË¢´ÊãíÁªù", "ËØ∑Âú®ËÆæÁΩÆ‰∏≠ÂºÄÂêØÂÆö‰ΩçÊùÉÈôê");
                setIsLoading(false);
                return;
            }
        }

        // 2. ‰ΩøÁî®ÂéüÁîü Geolocation Ëé∑ÂèñÁªèÁ∫¨Â∫¶
        Geolocation.getCurrentPosition(
            async (position) => {
                const { latitude, longitude } = position.coords;

                // 3. Ë∞ÉÁî®ÈÄÜÂú∞ÁêÜÁºñÁ†ÅÊé•Âè£ (ËøôÈáå‰ª•È´òÂæ∑ HTTP API ‰∏∫‰æãÔºå‰∏çÈúÄË¶Å SDK)
                // ÂéªÈ´òÂæ∑ÂºÄÊîæÂπ≥Âè∞Áî≥ËØ∑‰∏Ä‰∏™ WebÊúçÂä°Á±ªÂûãÁöÑ Key (ÂÖçË¥π)
                // const AMAP_KEY = '‰Ω†Áî≥ËØ∑ÁöÑWebÊúçÂä°Key';
                const AMAP_KEY = '‰Ω†Áî≥ËØ∑ÁöÑWebÊúçÂä°Key';

                const url = `https://restapi.amap.com/v3/geocode/regeo?output=json&location=${longitude},${latitude}&key=${AMAP_KEY}`;

                try {
                    const response = await fetch(url);
                    const result = await response.json();

                    if (result.status === '1') {
                        const addressComponent = result.regeocode.addressComponent;
                        // Êõ¥Êñ∞Áä∂ÊÄÅ
                        setProvince(addressComponent.province);
                        // Â§ÑÁêÜÁõ¥ËæñÂ∏ÇÔºöÂ¶ÇÊûúÊòØÂåó‰∫¨Ôºåcity ÂèØËÉΩÊòØÁ©∫Êï∞ÁªÑÊàñÂ≠óÁ¨¶‰∏≤ÔºåÈúÄË¶ÅÂÅöÂÖºÂÆπ
                        const cityName = typeof addressComponent.city === 'string'
                            ? addressComponent.city
                            : addressComponent.province;
                        setCity(cityName);
                    } else {
                        Alert.alert("Ëß£ÊûêÂ§±Ë¥•", "Êó†Ê≥ïËé∑ÂèñÂÖ∑‰ΩìÂüéÂ∏ÇÂêç");
                    }
                } catch (error) {
                    Alert.alert("ÁΩëÁªúÈîôËØØ", "Ëé∑Âèñ‰ΩçÁΩÆÂêçÁß∞Â§±Ë¥•");
                } finally {
                    setIsLoading(false);
                }
            },
            (error) => {
                Alert.alert("ÂÆö‰ΩçÂ§±Ë¥•", error.message);
                setIsLoading(false);
            },
            { enableHighAccuracy: false, timeout: 20000, maximumAge: 1000 }
        );
    };

    const handleBannerPress = (hotelId: string) => {
        navigation.navigate('HotelDetail', { hotelId });
    };



    // 1. Âú®ÁªÑ‰ª∂È°∂ÈÉ®Ê∑ªÂä†Áä∂ÊÄÅ
    const [selectedTags, setSelectedTags] = React.useState<string[]>([]);

    // 2. Ê∑ªÂä†ÁÇπÂáªÂ§ÑÁêÜÂáΩÊï∞
    const toggleTag = (tag: string) => {
        if (selectedTags.includes(tag)) {
            // Â¶ÇÊûúÂ∑≤ÁªèÈÄâ‰∏≠ÔºåÂàôÁßªÈô§ÔºàÂèñÊ∂àÁÇπ‰∫ÆÔºâ
            setSelectedTags(selectedTags.filter(t => t !== tag));
        } else {
            // Â¶ÇÊûúÊ≤°ÈÄâ‰∏≠ÔºåÂàôÊ∑ªÂä†ÔºàÁÇπ‰∫ÆÔºâ
            setSelectedTags([...selectedTags, tag]);
        }
    };

    const handleSearchKeyWordChange = (text: string) => {
        setSearchKeyWordValue(text);
    }



    return (
        <ScrollView style={styles.container} bounces={false} >
            {/* 1. È°∂ÈÉ® Banner Âå∫Âüü (‰øùÁïô) */}
            {/* 1. È°∂ÈÉ®ËΩÆÊí≠ÂõæÂå∫Âüü */}
            {/* 1. È°∂ÈÉ®ËΩÆÊí≠ÂõæÂå∫Âüü */}
            <View style={styles.bannerContainer}>
                <ScrollView
                    ref={scrollRef} // 4. ÁªëÂÆöÂºïÁî®
                    horizontal
                    pagingEnabled
                    showsHorizontalScrollIndicator={false}
                    onMomentumScrollEnd={handleScroll} // 5. Á°Æ‰øùÊâãÂä®ÊªëÂä®‰πüËÉΩÊõ¥Êñ∞ÁÇπ
                    bounces={false}
                >
                    {BANNERS.map((banner) => (
                        <TouchableOpacity
                            key={banner.id}
                            activeOpacity={0.9}
                            onPress={() => handleBannerPress(banner.id)}
                            style={{ width: width, height: 180 }}
