import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useState } from 'react';
import {
    ActivityIndicator,
    FlatList,
    Image,
    SafeAreaView,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';
import { styles } from './index.styles';

type Props = NativeStackScreenProps<RootStackParamList, 'HotelList'>;

// Ê®°ÊãüÈÖíÂ∫óÊï∞ÊçÆÁªìÊûÑ
interface Hotel {
    id: string;
    name: string;
    image: string;
    score: number;
    comments: number;
    address: string;
    tags: string[];
    price: number;
}

const HotelListScreen = ({ route, navigation }: Props) => {
    const { city, startDate, endDate } = route.params;
    const [hotels, setHotels] = useState<Hotel[]>([]);
    const [loading, setLoading] = useState(true);
    const [refreshing, setRefreshing] = useState(false);

    // Ê®°ÊãüÊï∞ÊçÆËé∑Âèñ
    const fetchHotels = (isMore = false) => {
        if (!isMore) setLoading(true);

        // Ê®°ÊãüÂª∂Ëøü
        setTimeout(() => {
            const newData: Hotel[] = Array.from({ length: 10 }).map((_, i) => ({
                id: `${hotels.length + i}`,
                name: `${city}ÂòâÊÇ¶Ë±™ÂçéÈÖíÂ∫ó ${hotels.length + i + 1}Âè∑Â∫ó`,
                image: 'https://images.unsplash.com/photo-1551882547-ff43c63efa9c?auto=format&fit=crop&w=300&q=80',
                score: 4.8,
                comments: 1200,
                address: 'Êµ∑Ê∑ÄÂå∫‰∏≠ÂÖ≥ÊùëÂ§ßË°ó15Âè∑ | Ë∑ùÊÇ®Áõ¥Á∫ø2.3km',
                tags: ['ÂÖçË¥πÂÅúËΩ¶', 'ÊûÅÈÄüWiFi', 'Ê¨¢ËøéÊ∞¥Êûú'],
                price: 588 + i * 10,
            }));

            setHotels(prev => isMore ? [...prev, ...newData] : newData);
            setLoading(false);
            setRefreshing(false);
        }, 1000);
    };

    useEffect(() => {
        fetchHotels();
    }, []);

    // Ê∏≤ÊüìÂçï‰∏™ÈÖíÂ∫óÈ°π
    const renderHotelItem = ({ item }: { item: Hotel }) => (
        <TouchableOpacity
            style={styles.hotelItem}
            onPress={() => navigation.navigate('HotelDetail', { hotelId: item.id, hotelName: item.name })}
        >
            <Image source={{ uri: item.image }} style={styles.hotelImage} />
            <View style={styles.infoArea}>
                <View>
                    <Text style={styles.hotelName} numberOfLines={2}>{item.name}</Text>
                    <View style={styles.scoreRow}>
                        <Text style={styles.scoreText}>{item.score}ÂàÜ</Text>
                        <Text style={styles.commentText}>{item.comments}Êù°ÁÇπËØÑ</Text>
                    </View>
                    <Text style={styles.addressText} numberOfLines={1}>{item.address}</Text>
                    <View style={styles.tagRow}>
                        {item.tags.map((tag, idx) => (
                            <View key={idx} style={styles.hotelTag}>
                                <Text style={styles.hotelTagText}>{tag}</Text>
                            </View>
                        ))}
                    </View>
                </View>
                <View style={styles.priceRow}>
                    <Text style={styles.priceSymbol}>¬•</Text>
                    <Text style={styles.priceText}>{item.price}</Text>
                    <Text style={styles.priceUnit}>Ëµ∑</Text>
                </View>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={styles.container}>
            {/* 1. È°∂ÈÉ®Ê†∏ÂøÉÁ≠õÈÄâÂ§¥ */}
            <View style={styles.headerContainer}>
                <TouchableOpacity style={styles.searchBar} onPress={() => navigation.goBack()}>
                    <Text style={styles.cityText}>{city}</Text>
                    <View style={styles.dateInfo}>
                        <Text style={styles.dateRangeText}>{startDate.split('-')[1]}Êúà{startDate.split('-')[2]}Êó• - {endDate.split('-')[1]}Êúà{endDate.split('-')[2]}Êó•</Text>
                        <Text style={styles.nightText}>ÂÖ±2Êôö</Text>
                    </View>
                    <Text style={{ color: '#007AFF' }}>üîç</Text>
                </TouchableOpacity>

                {/* 2. ËØ¶ÁªÜÁ≠õÈÄâÂå∫Âüü (Âê∏È°∂ÈÉ®ÂàÜ) */}
                <View style={styles.filterRow}>
                    {['Ê¨¢ËøéÂ∫¶ÊéíÂ∫è', '‰ΩçÁΩÆË∑ùÁ¶ª', '‰ª∑Ê†º/ÊòüÁ∫ß', 'Á≠õÈÄâ'].map((label, index) => (
                        <TouchableOpacity key={index} style={styles.filterItem}>
                            <Text style={styles.filterText}>{label}</Text>
                            <Text style={styles.arrowIcon}>‚ñº</Text>
                        </TouchableOpacity>
                    ))}
                </View>
            </View>

            {/* 3. ÈÖíÂ∫óÂàóË°® */}
            <FlatList
                data={hotels}
                renderItem={renderHotelItem}
                keyExtractor={item => item.id}
                onEndReached={() => fetchHotels(true)} // ‰∏äÊªëÂä†ËΩΩÊõ¥Â§ö
                onEndReachedThreshold={0.5}
                refreshing={refreshing}
                onRefresh={() => {
                    setRefreshing(true);
                    fetchHotels();
                }}
                ListFooterComponent={() => (
                    loading ? (
                        <View style={styles.footerLoading}>
                            <ActivityIndicator color="#007AFF" />
                        </View>
                    ) : null
                )}
            />
        </SafeAreaView>
    );
};

export default HotelListScreen;
