import { useNavigation } from '@react-navigation/native';
import { StackNavigationProp } from '@react-navigation/stack';
import React, { useState } from 'react';
import {
    Alert,
    Dimensions,
    Image,
    ScrollView,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View,
} from 'react-native';
import Icon from 'react-native-vector-icons/MaterialIcons';

const { width } = Dimensions.get('window');

type RootStackParamList = {
    HotelSearch: undefined;
    HotelList: {
        location: string;
        keyword: string;
        checkInDate: string;
        checkOutDate: string;
        filters: {
            starRating: number[];
            priceRange: [number, number];
        };
        tags: string[];
    };
    HotelDetail: { hotelId: string };
};

type HotelSearchPageNavigationProp = StackNavigationProp<RootStackParamList, 'HotelSearch'>;

interface Banner {
    id: string;
    image: string;
    title: string;
    hotelId: string;
}

interface QuickTag {
    id: string;
    name: string;
    icon: string;
}

const HotelSearchPage: React.FC = () => {
    const navigation = useNavigation<HotelSearchPageNavigationProp>();

    // 状态管理
    const [location, setLocation] = useState<string>('北京');
    const [keyword, setKeyword] = useState<string>('');
    const [checkInDate, setCheckInDate] = useState<string>('');
    const [checkOutDate, setCheckOutDate] = useState<string>('');
    const [selectedStars, setSelectedStars] = useState<number[]>([]);
    const [priceRange, setPriceRange] = useState<[number, number]>([0, 5000]);
    const [selectedTags, setSelectedTags] = useState<string[]>([]);

    // 模拟数据
    const banners: Banner[] = [
        {
            id: '1',
            image: 'https://via.placeholder.com/400x200?text=豪华酒店特惠',
            title: '豪华五星酒店限时8折',
            hotelId: 'hotel001',
        },
        {
            id: '2',
            image: 'https://via.placeholder.com/400x200?text=亲子度假套餐',
            title: '亲子度假套餐买一送一',
            hotelId: 'hotel002',
        },
        {
            id: '3',
            image: 'https://via.placeholder.com/400x200?text=商务酒店推荐',
            title: '商务出行首选酒店',
            hotelId: 'hotel003',
        },
    ];

    const quickTags: QuickTag[] = [
        { id: '1', name: '亲子', icon: 'child-friendly' },
        { id: '2', name: '豪华', icon: 'star' },
        { id: '3', name: '免费停车场', icon: 'local-parking' },
        { id: '4', name: '商务', icon: 'business-center' },
        { id: '5', name: '度假', icon: 'beach-access' },
        { id: '6', name: '温泉', icon: 'hot-tub' },
        { id: '7', name: '海景', icon: 'beach-access' },
        { id: '8', name: '机场接送', icon: 'airport-shuttle' },
    ];

    const starOptions = [1, 2, 3, 4, 5];

    // 处理Banner点击
    const handleBannerPress = (hotelId: string) => {
        navigation.navigate('HotelDetail', { hotelId });
    };

    // 处理查询按钮点击
    const handleSearch = () => {
        if (!checkInDate || !checkOutDate) {
            Alert.alert('提示', '请选择入住和退房日期');
            return;
        }

        navigation.navigate('HotelList', {
            location,
            keyword,
            checkInDate,
            checkOutDate,
            filters: {
                starRating: selectedStars,
                priceRange,
            },
            tags: selectedTags,
        });
    };

    // 处理星级选择
    const toggleStarRating = (star: number) => {
        setSelectedStars(prev =>
            prev.includes(star)
                ? prev.filter(s => s !== star)
                : [...prev, star]
        );
    };

    // 处理标签选择
    const toggleTag = (tagName: string) => {
        setSelectedTags(prev =>
            prev.includes(tagName)
                ? prev.filter(tag => tag !== tagName)
                : [...prev, tagName]
        );
    };

    // 模拟定位功能
    const handleLocationPress = () => {
        Alert.alert('定位', '正在获取当前位置...');
        setLocation('当前位置');
    };

    return (
        <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
            {/* 顶部Banner */}
            <View style={styles.bannerContainer}>
                <ScrollView horizontal showsHorizontalScrollIndicator={false} pagingEnabled>
                    {banners.map(banner => (
                        <TouchableOpacity
                            key={banner.id}
                            style={styles.banner}
                            onPress={() => handleBannerPress(banner.hotelId)}
                        >
                            <Image
                                source={{ uri: banner.image }}
                                style={styles.bannerImage}
                                resizeMode="cover"
                            />
                            <View style={styles.bannerOverlay}>
                                <Text style={styles.bannerTitle}>{banner.title}</Text>
                            </View>
                        </TouchableOpacity>
                    ))}
                </ScrollView>
            </View>

            {/* 核心查询区域 */}
            <View style={styles.searchSection}>
                <Text style={styles.sectionTitle}>酒店查询</Text>

                {/* 当前地点 */}
                <TouchableOpacity style={styles.inputContainer} onPress={handleLocationPress}>
                    <Icon name="location-on" size={20} color="#666" />
                    <Text style={styles.inputText}>{location}</Text>
                    <Icon name="keyboard-arrow-down" size={20} color="#666" />
                </TouchableOpacity>

                {/* 关键字搜索 */}
                <View style={styles.inputContainer}>
                    <Icon name="search" size={20} color="#666" />
                    <TextInput
                        style={styles.textInput}
                        placeholder="输入酒店名、地址或关键词"
                        value={keyword}
                        onChangeText={setKeyword}
                        placeholderTextColor="#999"
                    />
                </View>

                {/* 日期选择 */}
                <View style={styles.dateContainer}>
                    <TouchableOpacity style={styles.dateInput}>
                        <Icon name="calendar-today" size={16} color="#666" />
                        <Text style={styles.dateText}>
                            {checkInDate || '入住日期'}
                        </Text>
                    </TouchableOpacity>
                    <Text style={styles.dateSeparator}>-</Text>
                    <TouchableOpacity style={styles.dateInput}>
                        <Icon name="calendar-today" size={16} color="#666" />
                        <Text style={styles.dateText}>
                            {checkOutDate || '退房日期'}
                        </Text>
                    </TouchableOpacity>
                </View>

                {/* 筛选条件 - 酒店星级 */}
                <View style={styles.filterSection}>
                    <Text style={styles.filterTitle}>酒店星级</Text>
                    <View style={styles.starContainer}>
                        {starOptions.map(star => (
                            <TouchableOpacity
                                key={star}
                                style={[
                                    styles.starButton,
                                    selectedStars.includes(star) && styles.starButtonSelected
                                ]}
                                onPress={() => toggleStarRating(star)}
                            >
                                <Icon
                                    name="star"
                                    size={16}
                                    color={selectedStars.includes(star) ? '#ffd700' : '#ccc'}
                                />
                                <Text style={styles.starText}>{star}星</Text>
                            </TouchableOpacity>
                        ))}
                    </View>
                </View>

                {/* 快捷标签 */}
                <View style={styles.filterSection}>
                    <Text style={styles.filterTitle}>酒店属性</Text>
                    <View style={styles.tagsContainer}>
                        {quickTags.map(tag => (
                            <TouchableOpacity
                                key={tag.id}
                                style={[
                                    styles.tagButton,
                                    selectedTags.includes(tag.name) && styles.tagButtonSelected
                                ]}
                                onPress={() => toggleTag(tag.name)}
                            >
                                <Icon
                                    name={tag.icon as any}
                                    size={14}
                                    color={selectedTags.includes(tag.name) ? '#fff' : '#666'}
                                />
                                <Text style={[
                                    styles.tagText,
                                    selectedTags.includes(tag.name) && styles.tagTextSelected
                                ]}>
                                    {tag.name}
                                </Text>
                            </TouchableOpacity>
                        ))}
                    </View>
                </View>

                {/* 查询按钮 */}
                <TouchableOpacity style={styles.searchButton} onPress={handleSearch}>
                    <Text style={styles.searchButtonText}>查询酒店</Text>
                </TouchableOpacity>
            </View>
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    bannerContainer: {
        height: 200,
    },
    banner: {
        width: width - 32,
        height: 180,
        marginHorizontal: 16,
        borderRadius: 12,
        overflow: 'hidden',
        position: 'relative',
    },
    bannerImage: {
        width: '100%',
        height: '100%',
    },
    bannerOverlay: {
        position: 'absolute',
        bottom: 0,
        left: 0,
        right: 0,
        backgroundColor: 'rgba(0,0,0,0.5)',
        padding: 12,
    },
    bannerTitle: {
        color: '#fff',
        fontSize: 16,
        fontWeight: 'bold',
    },
    searchSection: {
        backgroundColor: '#fff',
        margin: 16,
        padding: 20,
        borderRadius: 12,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 4,
        elevation: 3,
    },
    sectionTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        marginBottom: 20,
        color: '#333',
    },
    inputContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        borderWidth: 1,
        borderColor: '#e0e0e0',
        borderRadius: 8,
        paddingHorizontal: 12,
        paddingVertical: 12,
        marginBottom: 16,
    },
    inputText: {
        flex: 1,
        marginLeft: 8,
        fontSize: 16,
        color: '#333',
    },
    textInput: {
        flex: 1,
        marginLeft: 8,
        fontSize: 16,
        color: '#333',
    },
    dateContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 20,
    },
    dateInput: {
        flex: 1,
