import { Button, Carousel, Flex } from '@ant-design/react-native';
import { Ionicons } from '@expo/vector-icons';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import {
    Dimensions,
    ImageBackground,
    ScrollView,
    StatusBar,
    StyleSheet,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const { width } = Dimensions.get('window');

// 模拟顶部 Banner 数据
const BANNERS = [
    {
        id: '101',
        img: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=800&q=80',
        title: '酒店7折起',
        hotelName: '悦榕庄度假酒店'
    },
    {
        id: '102',
        img: 'https://images.unsplash.com/photo-1582719508461-905c673771fd?auto=format&fit=crop&w=800&q=80',
        title: '宠粉节优惠',
        hotelName: '半岛酒店'
    },
];

const HomeScreen = ({ navigation }: Props) => {

    // 点击 Banner 跳转到详情页
    const handleBannerPress = (hotelId: string, hotelName: string) => {
        navigation.navigate('HotelDetail', {
            hotelId,
            hotelName
        });
    };

    return (
        <View style={styles.container}>
            {/* 使状态栏透明，文字为白色 */}
            <StatusBar barStyle="light-content" translucent backgroundColor="transparent" />

            <ScrollView
                contentContainerStyle={{ paddingBottom: 40 }}
                showsVerticalScrollIndicator={false}
                bounces={false}
            >

                {/* 1. 顶部 Banner 区域 */}
                <View style={styles.bannerContainer}>
                    <Carousel
                        autoplay
                        infinite
                        autoplayInterval={5000}
                        dotStyle={styles.dotStyle}
                        dotActiveStyle={styles.activeDotStyle}
                    >
                        {BANNERS.map((item) => (
                            <TouchableOpacity
                                key={item.id}
                                activeOpacity={1}
                                onPress={() => handleBannerPress(item.id, item.hotelName)}
                                style={styles.slide}
                            >
                                <ImageBackground source={{ uri: item.img }} style={styles.bannerImage}>
                                    <View style={styles.bannerOverlay}>
                                        <Text style={styles.bannerTitle}>{item.title}</Text>
                                        <View style={styles.bannerTag}><Text style={styles.bannerTagText}>宠物友好酒店</Text></View>
                                    </View>
                                </ImageBackground>
                            </TouchableOpacity>
                        ))}
                    </Carousel>
                </View>

                {/* 2. 核心查询区域 (悬浮卡片) */}
                <View style={styles.searchCard}>

                    {/* 业务分类 */}
                    <Flex style={styles.tabRow}>
                        <Text style={[styles.tabText, styles.tabActive]}>国内</Text>
                        <Text style={styles.tabText}>海外</Text>
                        <Text style={styles.tabText}>钟点房</Text>
                        <Text style={styles.tabText}>民宿</Text>
                    </Flex>

                    {/* a. 地点与搜索 */}
                    <View style={styles.inputRow}>
                        <TouchableOpacity style={styles.cityWrapper}>
                            <Text style={styles.cityText}>上海</Text>
                            <Ionicons name="caret-down" size={12} color="#333" />
                        </TouchableOpacity>

                        <View style={styles.separatorVertical} />

                        <Text style={styles.placeholderText}>位置/品牌/酒店</Text>

                        <TouchableOpacity style={styles.locateBtn}>
                            <Ionicons name="locate-outline" size={20} color="#007AFF" />
                        </TouchableOpacity>
                    </View>

                    <View style={styles.separatorHorizontal} />

                    {/* c. 入住日期选择 (暂为静态展示) */}
                    <TouchableOpacity style={styles.dateRow}>
                        <View style={styles.dateItem}>
                            <Text style={styles.dateMain}>01月09日 <Text style={styles.dateSub}>今天</Text></Text>
                        </View>
                        <View style={styles.nightCount}>
                            <Text style={styles.nightText}>共1晚</Text>
                        </View>
                        <View style={styles.dateItem}>
                            <Text style={styles.dateMain}>01月10日 <Text style={styles.dateSub}>明天</Text></Text>
                        </View>
                    </TouchableOpacity>

                    <View style={styles.separatorHorizontal} />

                    {/* d. 筛选条件 */}
                    <TouchableOpacity style={styles.filterRow}>
                        <Text style={styles.filterText}>价格/星级</Text>
                    </TouchableOpacity>

                    {/* e. 快捷标签 */}
                    <Flex wrap="wrap" style={styles.tagsRow}>
                        {['免费停车场', '上海浦东国际机场', '上海虹桥国际机场'].map((tag, index) => (
                            <View key={index} style={styles.tagItem}>
                                <Text style={styles.tagText}>{tag}</Text>
                            </View>
                        ))}
                    </Flex>

                    {/* 3. 查询按钮 */}
                    <Button
                        type="primary"
                        style={styles.searchButton}
                        onPress={() => navigation.navigate('HotelList', {
                            city: '上海',
                            startDate: '2024-01-09',
                            endDate: '2024-01-10'
                        })}
                    >
                        查 询
                    </Button>
                </View>

            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, backgroundColor: '#F5F7FA' },

    // Banner 样式
    bannerContainer: { height: 240 },
    slide: { height: 240 },
    bannerImage: { width: width, height: 240, justifyContent: 'flex-start' },
    bannerOverlay: { marginTop: 60, marginLeft: 20 },
    bannerTitle: { fontSize: 28, fontWeight: 'bold', color: '#fff', textShadowColor: 'rgba(0,0,0,0.3)', textShadowOffset: { width: 1, height: 1 }, textShadowRadius: 5 },
    bannerTag: { backgroundColor: 'rgba(255,255,255,0.8)', alignSelf: 'flex-start', paddingHorizontal: 6, paddingVertical: 2, borderRadius: 4, marginTop: 8 },
    bannerTagText: { fontSize: 10, color: '#333' },
    dotStyle: { backgroundColor: 'rgba(255,255,255,0.3)' },
    activeDotStyle: { backgroundColor: '#fff', width: 16 },

    // 查询卡片
    searchCard: {
        marginHorizontal: 12,
        marginTop: -40, // 关键：产生悬浮效果
        backgroundColor: '#fff',
        borderRadius: 12,
        padding: 16,
        elevation: 4,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.1,
        shadowRadius: 8,
    },
    tabRow: { marginBottom: 20 },
    tabText: { fontSize: 16, color: '#666', marginRight: 25 },
    tabActive: { color: '#007AFF', fontWeight: 'bold', borderBottomWidth: 2, borderBottomColor: '#007AFF', paddingBottom: 4 },

    inputRow: { flexDirection: 'row', alignItems: 'center', paddingVertical: 10 },
    cityWrapper: { flexDirection: 'row', alignItems: 'center' },
    cityText: { fontSize: 20, fontWeight: 'bold', color: '#333', marginRight: 4 },
    separatorVertical: { width: 1, height: 20, backgroundColor: '#EEE', marginHorizontal: 15 },
    placeholderText: { flex: 1, fontSize: 16, color: '#CCC' },
    locateBtn: { padding: 4 },

    separatorHorizontal: { height: 1, backgroundColor: '#F0F0F0', marginVertical: 10 },

    dateRow: { flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between', paddingVertical: 5 },
    dateItem: { flex: 1 },
    dateMain: { fontSize: 18, fontWeight: 'bold', color: '#333' },
    dateSub: { fontSize: 12, color: '#666', fontWeight: 'normal' },
    nightCount: { paddingHorizontal: 10, borderBottomWidth: 1, borderBottomColor: '#EEE' },
    nightText: { fontSize: 12, color: '#333' },

    filterRow: { paddingVertical: 12 },
    filterText: { fontSize: 16, color: '#CCC' },

    tagsRow: { marginTop: 5, marginBottom: 15 },
    tagItem: { backgroundColor: '#F5F7FA', paddingHorizontal: 10, paddingVertical: 6, borderRadius: 4, marginRight: 8, marginBottom: 8 },
    tagText: { fontSize: 12, color: '#666' },

    searchButton: {
        height: 50,
        borderRadius: 25,
        backgroundColor: '#007AFF',
        borderWidth: 0,
    }
});

export default HomeScreen;
