import { Ionicons } from '@expo/vector-icons';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useRef, useState } from 'react';
import {
    Dimensions,
    FlatList,
    ImageBackground,
    NativeScrollEvent,
    NativeSyntheticEvent,
    ScrollView,
    StatusBar,
    StyleSheet,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const { width } = Dimensions.get('window');

// 模拟顶部 Banner 数据
const BANNERS = [
    {
        id: '101',
        img: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=800&q=80',
        title: '酒店7折起',
        hotelName: '悦榕庄度假酒店'
    },
    {
        id: '102',
        img: 'https://images.unsplash.com/photo-1582719508461-905c673771fd?auto=format&fit=crop&w=800&q=80',
        title: '宠粉节优惠',
        hotelName: '半岛酒店'
    },
];

const HomeScreen = ({ navigation }: Props) => {
    const [activeIndex, setActiveIndex] = useState(0);
    const scrollRef = useRef<FlatList>(null);

    // 自动轮播逻辑
    useEffect(() => {
        const timer = setInterval(() => {
            let nextIndex = activeIndex === BANNERS.length - 1 ? 0 : activeIndex + 1;
            scrollRef.current?.scrollToIndex({ index: nextIndex, animated: true });
            setActiveIndex(nextIndex);
        }, 5000);
        return () => clearInterval(timer);
    }, [activeIndex]);

    const handleScroll = (event: NativeSyntheticEvent<NativeScrollEvent>) => {
        const x = event.nativeEvent.contentOffset.x;
        const index = Math.round(x / width);
        if (index !== activeIndex) {
            setActiveIndex(index);
        }
    };

    // 点击 Banner 跳转到详情页
    const handleBannerPress = (hotelId: string, hotelName: string) => {
        navigation.navigate('HotelDetail', {
            hotelId,
            hotelName
        });
    };

    return (
        <View style={styles.container}>
            <StatusBar barStyle="light-content" translucent backgroundColor="transparent" />

            <ScrollView
                contentContainerStyle={{ paddingBottom: 40 }}
                showsVerticalScrollIndicator={false}
                bounces={false}
            >
                {/* 1. 顶部 Banner 区域 (原生 FlatList 实现 Carousel) */}
                <View style={styles.bannerContainer}>
                    <FlatList
                        ref={scrollRef}
                        data={BANNERS}
                        horizontal
                        pagingEnabled
                        showsHorizontalScrollIndicator={false}
                        onMomentumScrollEnd={handleScroll}
                        renderItem={({ item }) => (
                            <TouchableOpacity
                                activeOpacity={1}
                                onPress={() => handleBannerPress(item.id, item.hotelName)}
                                style={styles.slide}
                            >
                                <ImageBackground source={{ uri: item.img }} style={styles.bannerImage}>
                                    <View style={styles.bannerOverlay}>
                                        <Text style={styles.bannerTitle}>{item.title}</Text>
                                        <View style={styles.bannerTag}>
                                            <Text style={styles.bannerTagText}>宠物友好酒店</Text>
                                        </View>
                                    </View>
                                </ImageBackground>
                            </TouchableOpacity>
                        )}
                        keyExtractor={(item) => item.id}
                    />
                    {/* 自定义指示点 */}
                    <View style={styles.dotContainer}>
                        {BANNERS.map((_, i) => (
                            <View
                                key={i}
                                style={[styles.dot, i === activeIndex ? styles.activeDot : null]}
                            />
                        ))}
                    </View>
                </View>

                {/* 2. 核心查询区域 (悬浮卡片) */}
                <View style={styles.searchCard}>
                    {/* 业务分类 Tab */}
                    <View style={styles.tabRow}>
                        <TouchableOpacity><Text style={[styles.tabText, styles.tabActive]}>国内</Text></TouchableOpacity>
                        <TouchableOpacity><Text style={styles.tabText}>海外</Text></TouchableOpacity>
                        <TouchableOpacity><Text style={styles.tabText}>钟点房</Text></TouchableOpacity>
                        <TouchableOpacity><Text style={styles.tabText}>民宿</Text></TouchableOpacity>
                    </View>

                    {/* a. 地点与搜索 */}
                    <View style={styles.inputRow}>
                        <TouchableOpacity style={styles.cityWrapper}>
                            <Text style={styles.cityText}>上海</Text>
                            <Ionicons name="caret-down" size={12} color="#333" />
                        </TouchableOpacity>

                        <View style={styles.separatorVertical} />

                        <View style={{ flex: 1 }}>
                            <Text style={styles.placeholderText}>位置/品牌/酒店</Text>
                        </View>

                        <TouchableOpacity style={styles.locateBtn}>
                            <Ionicons name="locate-outline" size={22} color="#007AFF" />
                        </TouchableOpacity>
                    </View>

                    <View style={styles.separatorHorizontal} />

                    {/* c. 入住日期选择 */}
                    <TouchableOpacity style={styles.dateRow}>
                        <View style={styles.dateItem}>
                            <Text style={styles.dateMain}>01月09日 <Text style={styles.dateSub}>今天</Text></Text>
                        </View>
                        <View style={styles.nightCount}>
                            <Text style={styles.nightText}>共1晚</Text>
                        </View>
                        <View style={[styles.dateItem, { alignItems: 'flex-end' }]}>
                            <Text style={styles.dateMain}>01月10日 <Text style={styles.dateSub}>明天</Text></Text>
                        </View>
                    </TouchableOpacity>

                    <View style={styles.separatorHorizontal} />

                    {/* d. 筛选条件 */}
                    <TouchableOpacity style={styles.filterRow}>
                        <Text style={styles.filterText}>价格/星级</Text>
                    </TouchableOpacity>

                    {/* e. 快捷标签 */}
                    <View style={styles.tagsRow}>
                        {['免费停车场', '上海浦东国际机场', '上海虹桥国际机场'].map((tag, index) => (
                            <View key={index} style={styles.tagItem}>
                                <Text style={styles.tagText}>{tag}</Text>
                            </View>
                        ))}
                    </View>

                    {/* 3. 查询按钮 (原生 Touchable 实现) */}
                    <TouchableOpacity
                        style={styles.searchButton}
                        activeOpacity={0.8}
                        onPress={() => navigation.navigate('HotelList', {
                            city: '上海',
                            startDate: '2024-01-09',
                            endDate: '2024-01-10'
                        })}
                    >
                        <Text style={styles.searchButtonText}>查 询</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, backgroundColor: '#F5F7FA' },

    // Banner 样式
    bannerContainer: { height: 260 },
    slide: { width: width, height: 260 },
    bannerImage: { width: width, height: 260, justifyContent: 'flex-start' },
    bannerOverlay: { marginTop: 65, marginLeft: 20 },
    bannerTitle: { fontSize: 28, fontWeight: 'bold', color: '#fff', textShadowColor: 'rgba(0,0,0,0.3)', textShadowOffset: { width: 1, height: 1 }, textShadowRadius: 5 },
    bannerTag: { backgroundColor: 'rgba(255,255,255,0.8)', alignSelf: 'flex-start', paddingHorizontal: 6, paddingVertical: 2, borderRadius: 4, marginTop: 8 },
    bannerTagText: { fontSize: 10, color: '#333' },
    dotContainer: { position: 'absolute', bottom: 55, width: '100%', flexDirection: 'row', justifyContent: 'center' },
    dot: { width: 6, height: 6, borderRadius: 3, backgroundColor: 'rgba(255,255,255,0.4)', marginHorizontal: 3 },
    activeDot: { backgroundColor: '#fff', width: 15 },

    // 查询卡片
    searchCard: {
        marginHorizontal: 15,
        marginTop: -45,
        backgroundColor: '#fff',
        borderRadius: 12,
        padding: 16,
        elevation: 8,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.1,
        shadowRadius: 10,
    },
    tabRow: { flexDirection: 'row', marginBottom: 20 },
    tabText: { fontSize: 16, color: '#666', marginRight: 25 },
    tabActive: { color: '#007AFF', fontWeight: 'bold', borderBottomWidth: 3, borderBottomColor: '#007AFF', paddingBottom: 4 },

    inputRow: { flexDirection: 'row', alignItems: 'center', paddingVertical: 5 },
    cityWrapper: { flexDirection: 'row', alignItems: 'center', minWidth: 60 },
    cityText: { fontSize: 22, fontWeight: 'bold', color: '#333', marginRight: 4 },
    separatorVertical: { width: 1, height: 24, backgroundColor: '#EEE', marginHorizontal: 15 },
    placeholderText: { fontSize: 16, color: '#CCC' },
    locateBtn: { padding: 4 },

    separatorHorizontal: { height: 1, backgroundColor: '#F2F2F2', marginVertical: 12 },

    dateRow: { flexDirection: 'row', alignItems: 'center', justifyContent: 'space-between' },
    dateItem: { flex: 1 },
    dateMain: { fontSize: 18, fontWeight: 'bold', color: '#333' },
    dateSub: { fontSize: 12, color: '#666', fontWeight: 'normal' },
    nightCount: { paddingHorizontal: 10, borderBottomWidth: 1, borderBottomColor: '#EEE', paddingBottom: 2 },
    nightText: { fontSize: 12, color: '#333' },

    filterRow: { paddingVertical: 8 },
    filterText: { fontSize: 16, color: '#CCC' },

    tagsRow: { flexDirection: 'row', flexWrap: 'wrap', marginTop: 10, marginBottom: 20 },
    tagItem: { backgroundColor: '#F5F7FA', paddingHorizontal: 10, paddingVertical: 6, borderRadius: 4, marginRight: 8, marginBottom: 8 },
    tagText: { fontSize: 12, color: '#666' },

    searchButton: {
        height: 50,
        borderRadius: 25,
        backgroundColor: '#007AFF',
        justifyContent: 'center',
        alignItems: 'center',
    },
    searchButtonText: {
        color: '#FFF',
        fontSize: 18,
        fontWeight: 'bold'
    }
});

export default HomeScreen;
