import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useState } from 'react';
import {
    ActivityIndicator,
    FlatList,
    Image,
    SafeAreaView,
    StatusBar,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';
import { styles } from './index.styles';

type Props = NativeStackScreenProps<RootStackParamList, 'HotelList'>;

interface Hotel {
    id: string;
    name: string;
    image: string;
    score: number;
    comments: number;
    address: string;
    tags: string[];
    price: number;
}

const HotelListScreen = ({ route, navigation }: Props) => {
    // Ëé∑ÂèñÈ¶ñÈ°µ‰º†ÈÄíÁöÑÂèÇÊï∞
    const { city = 'Âåó‰∫¨2', startDate = '05-01', endDate = '05-03' } = route.params || {};

    const [hotels, setHotels] = useState<Hotel[]>([]);
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);

    // Ê®°ÊãüÊï∞ÊçÆÂä†ËΩΩ
    const loadData = (isRefresh = false) => {
        if (loading) return;
        setLoading(true);

        setTimeout(() => {
            const mockData: Hotel[] = Array.from({ length: 5 }).map((_, i) => ({
                id: Math.random().toString(),
                name: `${city}ÂòâÊÇ¶Ë±™ÂçéÈÖíÂ∫ó ${isRefresh ? i + 1 : hotels.length + i + 1}Âè∑Â∫ó`,
                image: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=400&q=80',
                score: 4.8,
                comments: 1200,
                address: 'Êµ∑Ê∑ÄÂå∫‰∏≠ÂÖ≥ÊùëÂ§ßË°ó15Âè∑ | Ë∑ùÊÇ®Áõ¥Á∫ø2.3km',
                tags: ['ÂÖçË¥πÂÅúËΩ¶', 'ÊûÅÈÄüWiFi', 'Ê¨¢ËøéÊ∞¥Êûú'],
                price: 588 + (isRefresh ? i : hotels.length + i) * 10,
            }));

            setHotels(prev => isRefresh ? mockData : [...prev, ...mockData]);
            setLoading(false);
        }, 800);
    };

    useEffect(() => {
        loadData(true);
    }, []);

    const renderHotelItem = ({ item }: { item: Hotel }) => (
        <TouchableOpacity
            activeOpacity={0.9}
            style={styles.hotelItem}
            onPress={() => navigation.navigate('HotelDetail', { hotelId: item.id, hotelName: item.name })}
        >
            <Image source={{ uri: item.image }} style={styles.hotelImage} />
            <View style={styles.infoArea}>
                <View>
                    <Text style={styles.hotelName} numberOfLines={1}>{item.name}</Text>
                    <View style={styles.scoreRow}>
                        <Text style={styles.scoreText}>{item.score}ÂàÜ</Text>
                        <Text style={styles.commentText}>{item.comments}Êù°ÁÇπËØÑ</Text>
                    </View>
                    <Text style={styles.addressText} numberOfLines={1}>{item.address}</Text>
                    <View style={styles.tagRow}>
                        {item.tags.map((tag, idx) => (
                            <View key={idx} style={styles.hotelTag}>
                                <Text style={styles.hotelTagText}>{tag}</Text>
                            </View>
                        ))}
                    </View>
                </View>
                <View style={styles.priceContainer}>
                    <Text style={styles.priceSymbol}>¬•</Text>
                    <Text style={styles.priceText}>{item.price}</Text>
                    <Text style={styles.priceUnit}>Ëµ∑</Text>
                </View>
            </View>
        </TouchableOpacity>
    );

    return (
        <SafeAreaView style={styles.container}>
            <StatusBar barStyle="dark-content" backgroundColor="#fff" />

            {/* 1. È°∂ÈÉ®Ëá™ÂÆö‰πâÊêúÁ¥¢Â§¥ */}
            <View style={styles.headerContainer}>
                <View style={styles.searchBar}>
                    <TouchableOpacity onPress={() => navigation.goBack()}>
                        <Text style={styles.backIcon}>{'<'}</Text>
                    </TouchableOpacity>

                    <Text style={styles.cityText}>{city}</Text>

                    <View style={styles.dateInfo}>
                        <Text style={styles.dateRangeText}>{startDate} - {endDate}</Text>
                        <Text style={styles.nightText}>ÂÖ±2Êôö</Text>
                    </View>

                    <TouchableOpacity>
                        <Text style={styles.searchIcon}>üîç</Text>
                    </TouchableOpacity>
                </View>

                {/* 2. Á≠õÈÄâTabÊ†è */}
                <View style={styles.filterRow}>
                    {[
                        { label: 'Ê¨¢ËøéÂ∫¶ÊéíÂ∫è', active: true },
                        { label: '‰ΩçÁΩÆË∑ùÁ¶ª', active: false },
                        { label: '‰ª∑Ê†º/ÊòüÁ∫ß', active: false },
                        { label: 'Á≠õÈÄâ', active: false }
                    ].map((item, index) => (
                        <TouchableOpacity key={index} style={styles.filterItem}>
                            <Text style={[styles.filterText, item.active && { color: '#333', fontWeight: 'bold' }]}>
                                {item.label}
                            </Text>
                            <Text style={styles.arrowIcon}>‚ñº</Text>
                        </TouchableOpacity>
                    ))}
                </View>
            </View>

            {/* 3. ÈÖíÂ∫óÂàóË°® */}
            <FlatList
                data={hotels}
                renderItem={renderHotelItem}
                keyExtractor={item => item.id}
                onEndReached={() => loadData()} // ‰∏äÊªëËá™Âä®Âä†ËΩΩ
                onEndReachedThreshold={0.2}
                showsVerticalScrollIndicator={false}
                ListFooterComponent={
                    loading ? (
                        <View style={styles.footerLoading}>
                            <ActivityIndicator color="#007AFF" />
                        </View>
                    ) : <View style={{ height: 20 }} />
                }
            />
        </SafeAreaView>
    );
};

export default HotelListScreen;
