                <Image source={{ uri: 'https://via.placeholder.com/400x200' }} style={styles.banner} />
                <Text style={styles.imageCount}>1/10 Âº†</Text>
            </View>

            {/* 3. ÈÖíÂ∫óÂü∫Á°Ä‰ø°ÊÅØ */}
            <View style={styles.section}>
