            </View>

            {/* 4. Êó•ÂéÜ+Èó¥Â§ú Banner (Âç†‰Ωç) */}
            <View style={[styles.section, { backgroundColor: '#e6f7ff' }]}>
                <Text>üìÖ 05Êúà01Êó• - 05Êúà02Êó• (ÂÖ±1Êôö)</Text>
            </View>

            {/* 5. ÊàøÂûã‰ª∑Ê†ºÂàóË°® */}
            <View style={styles.section}>
                <Text style={styles.sectionTitle}>ÊàøÂûãÈ¢ÑÂÆö</Text>
                {roomTypes.map(room => (
                    <View key={room.id} style={styles.roomItem}>
                        <View>
                            <Text style={styles.roomName}>{room.name}</Text>
                            <Text style={styles.roomStatus}>{room.status}</Text>
                        </View>
                        <View style={{ alignItems: 'flex-end' }}>
                            <Text style={styles.roomPrice}>Ôø•{room.price}</Text>
                            <Button title="È¢ÑÂÆö" onPress={() => alert('Ë∑≥ËΩ¨ÊîØ‰ªòÊµÅÁ®ã')} />
                        </View>
                    </View>
                ))}
            </View>
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: { flex: 1, backgroundColor: '#fff' },
    bannerContainer: { position: 'relative' },
    banner: { width: '100%', height: 200 },
    imageCount: { position: 'absolute', bottom: 10, right: 10, color: '#fff', backgroundColor: 'rgba(0,0,0,0.5)', padding: 4, borderRadius: 4 },
    section: { padding: 15, borderBottomWidth: 1, borderBottomColor: '#eee' },
    title: { fontSize: 22, fontWeight: 'bold' },
    subText: { color: '#666', marginVertical: 5 },
    address: { fontSize: 13, color: '#444' },
    sectionTitle: { fontSize: 18, fontWeight: 'bold', marginBottom: 10 },
    roomItem: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', paddingVertical: 15, borderBottomWidth: 0.5, borderBottomColor: '#f0f0f0' },
    roomName: { fontSize: 16, fontWeight: '500' },
    roomStatus: { color: '#52c41a', fontSize: 12 },
    roomPrice: { fontSize: 20, color: '#ff4d4f', fontWeight: 'bold', marginBottom: 5 }
