import { RootStackParamList } from '../../navigation/types';
import { styles } from './index.styles';

type Props = NativeStackScreenProps<RootStackParamList, 'HotelDetail'>;

const HotelDetailScreen = ({ route, navigation }: Props) => {
    const { hotelId, hotelName } = route.params;

    // Ê®°ÊãüÊàøÂûãÊï∞ÊçÆ (Â¢ûÂä†ÂõæÁâáÂíåËØ¶ÁªÜËßÑÊ†º)
    const roomTypes = [
        { id: '1', name: 'ÁªèÂÖ∏ÂèåÂ∫äÊàø', price: 936, specs: '2Âº†1.2Á±≥Âçï‰∫∫Â∫ä | 40m¬≤ | 2‰∫∫ÂÖ•‰Ωè', img: 'https://via.placeholder.com/150/666' },
        { id: '2', name: 'Ë±™ÂçéÂ§ßÂ∫äÊàø', price: 1280, specs: '1Âº†1.8Á±≥Â§ßÂ∫ä | 45m¬≤ | 2‰∫∫ÂÖ•‰Ωè', img: 'https://via.placeholder.com/150/444' },
        { id: '3', name: 'Ë°åÊîøÂ•óÊàø', price: 2560, specs: '1Âº†2.0Á±≥ÁâπÂ§ßÂ∫ä | 80m¬≤ | 2‰∫∫ÂÖ•‰Ωè', img: 'https://via.placeholder.com/150/222' },
    ];

    // ÈÄªËæëÔºöÊåâ‰ª∑Ê†º‰ªé‰ΩéÂà∞È´òÊéíÂ∫è
    const sortedRooms = useMemo(() => {
        return [...roomTypes].sort((a, b) => a.price - b.price);
    }, []);

    return (
        <View style={styles.container}>
            <StatusBar barStyle="light-content" translucent backgroundColor="transparent" />

            {/* 1. È°∂ÈÉ®ÂØºËà™ (Ê≤âÊµ∏Âºè) */}
            <View style={styles.headerOverlay}>
                <TouchableOpacity onPress={() => navigation.goBack()}>
                    <Text style={styles.backText}>‚Äπ</Text>
