            <ScrollView bounces={false} showsVerticalScrollIndicator={false}>
                {/* 2. Â§ßÂõæ Banner */}
                <View style={styles.bannerContainer}>
                    <Image
                        source={{ uri: 'https://via.placeholder.com/800x600/333' }}
                        style={styles.bannerImage}
                    />
                </View>

                {/* 3. ÈÖíÂ∫óÂü∫Á°Ä‰ø°ÊÅØÂç°Áâá */}
                <View style={styles.card}>
                    <Text style={styles.hotelName}>{hotelName}</Text>
                    <View style={styles.starRow}>
                        <Text style={styles.starText}>‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</Text>
                        <View style={styles.rankingTag}>
                            <Text style={styles.rankingText}>‰∏äÊµ∑ÁæéÊôØÈÖíÂ∫óÊ¶ú No.16 „Äâ</Text>
                        </View>
                    </View>

                    {/* ËÆæÊñΩÁÆÄËø∞ */}
                    <View style={styles.facilityContainer}>
                        {[
                            { n: '2020Âπ¥ÂºÄ‰∏ö', i: 'üè¢' },
                            { n: 'Êñ∞‰∏≠ÂºèÈ£é', i: 'üèÆ' },
                            { n: 'ÂÖçË¥πÂÅúËΩ¶', i: 'üÖøÔ∏è' },
                            { n: '‰∏ÄÁ∫øÊ±üÊôØ', i: 'üåä' },
                            { n: 'ËÆæÊñΩÊîøÁ≠ñ', i: '‚û°Ô∏è' },
