                            </View>
                        ))}
                    </View>

                    {/* Âú∞ÂùÄ */}
                    <View style={styles.addressRow}>
                        <Text style={styles.addressText}>Êµ¶‰∏úÊñ∞Âå∫Êµ¶ÊòéË∑Ø868ÂºÑ3Âè∑Ê•º ¬∑ Ë∑ùÂ°òÊ°•Âú∞ÈìÅÁ´ôÊ≠•Ë°å1.5ÂÖ¨Èáå</Text>
                    </View>
                </View>

                {/* 4. Êó•ÂéÜÈó¥Â§úÈÄâÊã© */}
                <View style={styles.calendarCard}>
                    <View style={styles.dateRow}>
                        <View style={styles.dateBox}>
                            <Text style={styles.dateLarge}>1Êúà9Êó•</Text>
                            <Text style={styles.dateSmall}>‰ªäÂ§©</Text>
                        </View>
                        <Text style={styles.nightCount}>1Êôö</Text>
                        <View style={styles.dateBox}>
                            <Text style={styles.dateLarge}>1Êúà10Êó•</Text>
                            <Text style={styles.dateSmall}>ÊòéÂ§©</Text>
                        </View>
                        <Text style={{ color: '#999' }}>„Äâ</Text>
                    </View>
                </View>

                {/* 5. ÊàøÂûã‰ª∑Ê†ºÂàóË°® */}
                {sortedRooms.map((room) => (
                    <TouchableOpacity key={room.id} style={styles.roomItem}>
                        <Image source={{ uri: room.img }} style={styles.roomImage} />
                        <View style={styles.roomInfo}>
                            <View>
                                <Text style={styles.roomName}>{room.name}</Text>
                                <Text style={styles.roomSpecs}>{room.specs}</Text>
                            </View>
                            <View style={styles.roomPriceRow}>
                                <View style={{ flexDirection: 'row', alignItems: 'baseline' }}>
                                    <Text style={styles.priceSymbol}>Ôø•</Text>
                                    <Text style={styles.priceText}>{room.price}</Text>
                                    <Text style={{ fontSize: 12, color: '#999' }}> Ëµ∑</Text>
                                </View>
                                <View style={styles.bookBtn}>
                                    <Text style={styles.bookBtnText}>È¢ÑËÆ¢</Text>
                                </View>
                            </View>
