import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import {
    Dimensions,
    Image,
    ScrollView,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const { width } = Dimensions.get('window');

const HomeScreen = ({ navigation }: Props) => {

    const handleBannerPress = (hotelId: string) => {
        navigation.navigate('HotelDetail', { hotelId, hotelName: 'ÁâπÊÉ†ÈÖíÂ∫óËØ¶ÊÉÖ' });
    };

    // Ê®°ÊãüÂø´Êç∑Ê†áÁ≠æÊï∞ÊçÆ
    const quickTags = ['‰∫≤Â≠êÈÖíÂ∫ó', 'Ë±™ÂçéÈÖíÂ∫ó', 'ÂÖçË¥πÂÅúËΩ¶Âú∫', 'Âê´Êó©È§ê', 'ËøëÂú∞ÈìÅ', 'Ê∏©Ê≥âÈÖíÂ∫ó'];

    return (
        <ScrollView style={styles.container} bounces={false}>
            {/* 1. È°∂ÈÉ® Banner */}
            <TouchableOpacity
                activeOpacity={0.9}
                onPress={() => handleBannerPress('1001')}
                style={styles.bannerContainer}
            >
                <Image
                    source={{ uri: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=800&q=80' }}
                    style={styles.bannerImage}
                />
                <View style={styles.bannerMask}>
                    <Text style={styles.bannerTitle}>Êò•Â≠£ÁâπÊÉ†Ôºö‰∏â‰∫öÊµ∑ÊôØÊàø 5 ÊäòËµ∑</Text>
                </View>
            </TouchableOpacity>

            {/* 2. Â¢ûÂº∫ÁâàÊü•ËØ¢Âç°Áâá */}
            <View style={styles.searchCard}>

                {/* a. ÁõÆÁöÑÂú∞‰∏éÂÆö‰Ωç */}
                <View style={styles.locationRow}>
                    <TouchableOpacity style={{ flex: 1 }}>
                        <Text style={styles.label}>ÁõÆÁöÑÂú∞</Text>
                        <Text style={styles.searchValue}>Âåó‰∫¨</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.locationBtn}>
                        <Text style={styles.locationIcon}>üìç</Text>
                        <Text style={styles.locationText}>ÊàëÁöÑ‰ΩçÁΩÆ</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.divider} />

                {/* c. ÂÖ•‰ΩèÊó•ÊúüÈÄâÊã© (Âå∫Èó¥Â±ïÁ§∫) */}
                <TouchableOpacity style={styles.dateRow}>
                    <View style={{ flex: 1 }}>
                        <Text style={styles.label}>ÂÖ•‰Ωè</Text>
                        <Text style={styles.dateText}>05Êúà01Êó• <Text style={styles.weekText}>Âë®‰∏â</Text></Text>
                    </View>
                    <View style={styles.nightBadge}>
                        <Text style={styles.nightText}>2Êôö</Text>
                    </View>
                    <View style={{ flex: 1, alignItems: 'flex-end' }}>
                        <Text style={styles.label}>Á¶ªÂ∫ó</Text>
                        <Text style={styles.dateText}>05Êúà03Êó• <Text style={styles.weekText}>Âë®‰∫î</Text></Text>
                    </View>
                </TouchableOpacity>

                <View style={styles.divider} />

                {/* b. ÂÖ≥ÈîÆÂ≠óÊêúÁ¥¢ */}
                <View style={styles.searchItem}>
                    <Text style={styles.label}>ÂÖ≥ÈîÆËØç/ÈÖíÂ∫ó/Âú∞ÂùÄ</Text>
                    <TextInput
                        placeholder="ÊêúÁ¥¢‰Ω†ÊÉ≥ÂéªÁöÑÂú∞ÁÇπ"
                        placeholderTextColor="#ccc"
                        style={styles.searchInput}
                    />
                </View>

                <View style={styles.divider} />

                {/* d. Á≠õÈÄâÊù°‰ª∂ (‰ª∑Ê†º/ÊòüÁ∫ß) */}
                <TouchableOpacity style={styles.searchItem}>
                    <Text style={styles.label}>‰ª∑Ê†º/ÊòüÁ∫ß</Text>
                    <Text style={styles.searchValue}>Ôø•200-500, ÂõõÊòü/È´òÊ°£</Text>
                </TouchableOpacity>

                {/* e. Âø´Êç∑Ê†áÁ≠æ (Ê®™ÂêëÊªöÂä®) */}
                <ScrollView
                    horizontal
                    showsHorizontalScrollIndicator={false}
                    style={styles.tagScroll}
                >
                    {quickTags.map((tag, index) => (
                        <TouchableOpacity key={index} style={styles.tagItem}>
                            <Text style={styles.tagText}>{tag}</Text>
                        </TouchableOpacity>
                    ))}
                </ScrollView>

                {/* Á´ãÂç≥Êü•ËØ¢ÊåâÈíÆ */}
                <TouchableOpacity
                    style={styles.searchButton}
                    onPress={() => navigation.navigate('HotelList', {
                        city: 'Âåó‰∫¨',
                        startDate: '2024-05-01',
                        endDate: '2024-05-03'
                    })}
                >
                    <Text style={styles.searchButtonText}>Á´ãÂç≥Êü•ËØ¢</Text>
                </TouchableOpacity>
            </View>

            <View style={styles.infoSection}>
                <Text style={styles.infoText}>¬∑ ÂÆòÊñπÁõ¥Ëê• ¬∑ ÊûÅÈÄüÁ°ÆËÆ§ ¬∑ Êó†ÂøßÈÄÄÊîπ</Text>
            </View>
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    bannerContainer: {
        width: width,
        height: 180,
    },
    bannerImage: {
        width: '100%',
        height: '100%',
    },
    bannerMask: {
        position: 'absolute',
        bottom: 0,
        width: '100%',
        padding: 15,
        backgroundColor: 'rgba(0,0,0,0.3)',
    },
    bannerTitle: {
        color: '#fff',
        fontSize: 18,
        fontWeight: 'bold',
    },
    searchCard: {
        margin: 15,
        marginTop: -20,
        backgroundColor: '#fff',
        borderRadius: 12,
        padding: 16,
        elevation: 4,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 10,
    },
    // ÁõÆÁöÑÂú∞‰∏éÂÆö‰ΩçË°å
    locationRow: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 10,
    },
    locationBtn: {
        flexDirection: 'row',
        alignItems: 'center',
        borderLeftWidth: 1,
        borderLeftColor: '#eee',
        paddingLeft: 15,
    },
    locationIcon: { fontSize: 16, marginRight: 4 },
    locationText: { color: '#007AFF', fontWeight: '500' },

    // Êó•ÊúüË°å
    dateRow: {
        flexDirection: 'row',
        alignItems: 'center',
        paddingVertical: 12,
    },
    dateText: { fontSize: 18, fontWeight: '600', color: '#333', marginTop: 4 },
    weekText: { fontSize: 12, fontWeight: 'normal', color: '#999' },
    nightBadge: {
        borderWidth: 1,
        borderColor: '#eee',
        borderRadius: 10,
        paddingHorizontal: 8,
        height: 20,
        justifyContent: 'center',
    },
    nightText: { fontSize: 10, color: '#666' },

    // ËæìÂÖ•Ê°Ü
    searchInput: {
        fontSize: 16,
        padding: 0,
        marginTop: 4,
        color: '#333',
        fontWeight: '500',
    },

    // Ê†áÁ≠æÊ†∑Âºè
    tagScroll: {
        marginHorizontal: -16, // ÊäµÊ∂àÂç°ÁâáÂÜÖËæπË∑ùÔºå‰ΩøÊªöÂä®Êù°Ë¥¥Ëæπ
        paddingHorizontal: 16,
        marginTop: 15,
    },
    tagItem: {
        backgroundColor: '#f2f2f2',
        paddingHorizontal: 12,
        paddingVertical: 6,
        borderRadius: 4,
        marginRight: 8,
    },
    tagText: { fontSize: 12, color: '#666' },

    searchItem: { paddingVertical: 12 },
    label: { fontSize: 12, color: '#999' },
    searchValue: { fontSize: 16, fontWeight: '600', color: '#333', marginTop: 4 },
    divider: { height: 1, backgroundColor: '#f0f0f0' },

    searchButton: {
        backgroundColor: '#007AFF',
        borderRadius: 8,
        height: 48,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 20,
    },
    searchButtonText: { color: '#fff', fontSize: 18, fontWeight: 'bold' },
    infoSection: { alignItems: 'center', marginVertical: 20 },
    infoText: { color: '#ccc', fontSize: 12 }
});

export default HomeScreen;
