import Geolocation from '@react-native-community/geolocation';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React, { useEffect, useRef, useState } from 'react';
import {
    Alert,
    FlatList,
    Image,
    Modal,
    PermissionsAndroid,
    Platform,
    ScrollView,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native';

// ÂØºÂÖ•Á¨¨‰∏âÊñπÊï∞ÊçÆÂåÖ
import citiesRaw from 'china-division/dist/cities.json';
import provincesRaw from 'china-division/dist/provinces.json';

import Calendar_My from '../../components/Calendar_My'; // Á°Æ‰øùË∑ØÂæÑÊ≠£Á°Æ
import { RootStackParamList } from '../../navigation/types';

// import Calendar_My from '.../components/Calendar_My';
import { styles, width } from './index.styles';

// --- Êï∞ÊçÆÈ¢ÑÂ§ÑÁêÜ ---
const provinceList = provincesRaw.map(p => p.name);
const cityMap: Record<string, string[]> = {};
provincesRaw.forEach(p => {
    const matchedCities = citiesRaw.filter(c => c.provinceCode === p.code).map(c => c.name);
    cityMap[p.name] = matchedCities;
});

const FILTER_DATA = {
    prices: ['‰∏çÈôê', 'Ôø•0-150', 'Ôø•150-300', 'Ôø•300-600', 'Ôø•600-1000', 'Ôø•1000‰ª•‰∏ä'],
    roomTypes: ['‰∏çÈôê', 'Â§ßÂ∫äÊàø', 'Âèå‰∫∫Âª∫Á≠ë', 'Âçï‰∫∫Â∫ä', '‰∏â‰∫∫Èó¥', 'Â•óÊàø']
};

const BANNERS = [
    { id: '1', title: 'Êò•Â≠£ÁâπÊÉ†Ôºö‰∏â‰∫öÊµ∑ÊôØÊàø 5 ÊäòËµ∑', uri: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=800' },
    { id: '2', title: 'Ê∑±Â±±ÈÅøÊöëÔºöËé´Âπ≤Â±±Á≤æÂìÅÊ∞ëÂÆø', uri: 'https://images.unsplash.com/photo-1582719478250-c89cae4dc85b?w=800' },
    { id: '3', title: 'È≠îÈÉΩ‰πãÂ§úÔºö‰∏äÊµ∑Â§ñÊª©ÊôØËßÇÊàø', uri: 'https://images.unsplash.com/photo-1506059612708-99d6c258160e?w=800' },
    { id: '4', title: 'Âè§ÂüéÈüµÂë≥ÔºöÂ§ßÁêÜÊ¥±Êµ∑Èò≥ÂÖâÊàø', uri: 'https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?w=800' },
    { id: '5', title: '‰∫≤Â≠êÊó∂ÂÖâÔºöÈïøÈöÜ‰∏ªÈ¢òÈÖíÂ∫ó', uri: 'https://images.unsplash.com/photo-1520250497591-112f2f40a3f4?w=800' },
];

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const HomeScreen = ({ navigation }: Props) => {
    // --- Áä∂ÊÄÅÁÆ°ÁêÜ ---
    const scrollRef = useRef<ScrollView>(null);
    const [activeIndex, setActiveIndex] = useState(0);
    const [isLoading, setIsLoading] = useState(false);

    // ÊêúÁ¥¢Ë°®ÂçïÁä∂ÊÄÅ
    const [province, setProvince] = useState('Âπø‰∏úÁúÅ');
    const [city, setCity] = useState('Áè†Êµ∑Â∏Ç');
    const [price, setPrice] = useState('Ôø•150-300');
    const [roomType, setRoomType] = useState('Â§ßÂ∫äÊàø');
    const [searchKeyWordValue, setSearchKeyWordValue] = useState('');
    const [selectedTags, setSelectedTags] = useState<string[]>([]);

    // Êó•ÊúüÁä∂ÊÄÅ
    const [calendarVisible, setCalendarVisible] = useState(false);
    const [dateInfo, setDateInfo] = useState({
        startDisplay: '02Êúà03Êó•',
        startWeek: 'Âë®‰∫å',
        startStr: '2026-02-03',
        endDisplay: '02Êúà05Êó•',
        endWeek: 'Âë®Âõõ',
        endStr: '2026-02-05',
        nights: 2
    });

    // ÈÄâÊã©Âô®ÊéßÂà∂
    const [modalVisible, setModalVisible] = useState(false);
    const [pickingType, setPickingType] = useState<'province' | 'city' | 'price' | 'roomType'>('province');



    // --- Ëá™Âä®Êí≠ÊîæÈÄªËæë ---
    useEffect(() => {
        const timer = setInterval(() => {
            let nextIndex = activeIndex + 1;
            if (nextIndex >= BANNERS.length) {
                nextIndex = 0; // ÂõûÂà∞Á¨¨‰∏ÄÂº†
            }

            // ÊªöÂä®Âà∞ÊåáÂÆö‰ΩçÁΩÆ
            scrollRef.current?.scrollTo({
                x: nextIndex * width,
                animated: true,
            });

            setActiveIndex(nextIndex);
        }, 10000);

        return () => clearInterval(timer);
    }, [activeIndex]);

    // --- ÊâãÂä®ÊªëÂä®Êó∂Êõ¥Êñ∞ÂúÜÁÇπ ---
    const handleScroll = (event: any) => {
        const x = event.nativeEvent.contentOffset.x;
        const index = Math.round(x / width);
        if (index !== activeIndex) {
            setActiveIndex(index);
        }
    };

    // --- 2. ÂÆö‰ΩçÈÄªËæë ---
    const handleLocationPress = async () => {
        setIsLoading(true);
        if (Platform.OS === 'android') {
            const granted = await PermissionsAndroid.request(PermissionsAndroid.PERMISSIONS.ACCESS_FINE_LOCATION);
            if (granted !== PermissionsAndroid.RESULTS.GRANTED) {
                Alert.alert("ÊùÉÈôêÊãíÁªù", "ËØ∑ÂºÄÂêØÂÆö‰ΩçÊùÉÈôê");
                setIsLoading(false);
                return;
            }
        }
        Geolocation.getCurrentPosition(async (pos) => {
            try {
                const AMAP_KEY = '‰Ω†ÁöÑÈ´òÂæ∑WebÊúçÂä°Key'; // ËÆ∞ÂæóÊõøÊç¢
                const url = `https://restapi.amap.com/v3/geocode/regeo?location=${pos.coords.longitude},${pos.coords.latitude}&key=${AMAP_KEY}`;
                const res = await (await fetch(url)).json();
                if (res.status === '1') {
                    const comp = res.regeocode.addressComponent;
                    setProvince(comp.province);
                    setCity(typeof comp.city === 'string' ? comp.city : comp.province);
                }
            } catch (e) { Alert.alert("ÁΩëÁªúÈîôËØØ"); }
            finally { setIsLoading(false); }
        }, () => setIsLoading(false), { timeout: 15000 });
    };

    // --- 3. ÈÄâÊã©Âô®ÈÄªËæë ---
    const openPicker = (type: typeof pickingType) => {
        setPickingType(type);
        setModalVisible(true);
    };

    const handleSelect = (item: string) => {
        if (pickingType === 'province') {
            setProvince(item);
            setCity(cityMap[item][0] || item);
        } else if (pickingType === 'city') setCity(item);
        else if (pickingType === 'price') setPrice(item);
        else if (pickingType === 'roomType') setRoomType(item);
        setModalVisible(false);
    };

    const getModalData = () => {
        if (pickingType === 'province') return provinceList;
        if (pickingType === 'city') return cityMap[province] || [];
        if (pickingType === 'price') return FILTER_DATA.prices;
        return FILTER_DATA.roomTypes;
    };

    // --- Ê∏≤ÊüìÈÉ®ÂàÜ ---
    return (
        <ScrollView style={styles.container} bounces={false}>
            {/* 1. ËΩÆÊí≠Âõæ */}
            {/* 1. È°∂ÈÉ®ËΩÆÊí≠ÂõæÂå∫Âüü */}
            <View style={styles.bannerContainer}>
                <ScrollView
                    ref={scrollRef}
                    horizontal
                    pagingEnabled
                    showsHorizontalScrollIndicator={false}
                    onMomentumScrollEnd={handleScroll} // ÊâãÂä®ÊªëÂä®ÂõûË∞É
                    bounces={false}
                >
                    {BANNERS.map((banner) => (
                        <TouchableOpacity
                            key={banner.id}
                            activeOpacity={0.9}
                            onPress={() => navigation.navigate('HotelDetail', { hotelId: banner.id })}
                            style={{ width: width, height: 180 }}
                        >
