                            <View style={styles.bannerMask}>
                                <Text style={styles.bannerTitle}>{banner.title}</Text>
                            </View>
                        </TouchableOpacity>
                    ))}
                </ScrollView>

                {/* ÊåáÁ§∫ÁÇπÂå∫Âüü */}
                <View style={styles.pagination}>
                    {BANNERS.map((_, i) => (
                        <View
                            key={i}
                            style={[
                                styles.dot,
                                activeIndex === i && styles.activeDot // ÂΩìÁ¥¢ÂºïÂåπÈÖçÊó∂Â∫îÁî®È´ò‰∫ÆÊ†∑Âºè
                            ]}
                        />
                    ))}
                </View>
            </View>

            {/* 2. Êü•ËØ¢Âç°Áâá */}
            <View style={styles.searchCard}>
                {/* ÂüéÂ∏ÇÂÆö‰Ωç */}
                <View style={styles.locationRow}>
                    <TouchableOpacity style={styles.locationItem} onPress={() => openPicker('province')}>
                        <Text style={styles.label}>ÁúÅ‰ªΩ</Text>
                        <Text style={styles.searchValue}>{province} ‚ñæ</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.locationItem} onPress={() => openPicker('city')}>
                        <Text style={styles.label}>ÂüéÂ∏Ç</Text>
                        <Text style={styles.searchValue}>{city} ‚ñæ</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.locationBtn} onPress={handleLocationPress} disabled={isLoading}>
                        <Text style={styles.locationIcon}>üìç</Text>
                        <Text style={styles.locationText}>{isLoading ? 'ÂÆö‰Ωç‰∏≠' : 'ÊàëÁöÑ‰ΩçÁΩÆ'}</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.divider} />

                {/* ÂÖ≥ÈîÆÂ≠ó */}
                <View style={styles.searchItem}>
                    <Text style={styles.label}>‰ΩçÁΩÆ/ÂìÅÁâå/ÈÖíÂ∫ó</Text>
                    <TextInput
                        placeholder="ÊêúÁ¥¢‰Ω†ÊÉ≥ÂéªÁöÑÂú∞ÁÇπ"
                        placeholderTextColor="#ccc"
                        style={styles.searchInput}
                        value={searchKeyWordValue}
                        onChangeText={setSearchKeyWordValue}
                    />
                </View>

                <View style={styles.divider} />

                {/* Êó•ÊúüÈÄâÊã© */}
                <TouchableOpacity style={styles.dateRow} onPress={() => setCalendarVisible(true)}>
                    <View style={{ flex: 1 }}>
                        <Text style={styles.label}>ÂÖ•‰Ωè</Text>
                        <Text style={styles.dateText}>{dateInfo.startDisplay} <Text style={styles.weekText}>{dateInfo.startWeek}</Text></Text>
                    </View>
                    <View style={styles.nightBadge}><Text style={styles.nightText}>{dateInfo.nights}Êôö</Text></View>
                    <View style={{ flex: 1, alignItems: 'flex-end' }}>
                        <Text style={styles.label}>Á¶ªÂ∫ó</Text>
                        <Text style={styles.dateText}>{dateInfo.endDisplay} <Text style={styles.weekText}>{dateInfo.endWeek}</Text></Text>
                    </View>
                </TouchableOpacity>

                <View style={styles.divider} />

                {/* ‰ª∑Ê†ºÊàøÂûã */}
                <View style={styles.locationRow}>
                    <TouchableOpacity style={styles.locationItem} onPress={() => openPicker('price')}>
                        <Text style={styles.label}>‰ª∑Ê†º</Text>
                        <Text style={styles.searchValue}>{price} ‚ñæ</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.locationItem} onPress={() => openPicker('roomType')}>
                        <Text style={styles.label}>ÊàøÂûã</Text>
                        <Text style={styles.searchValue}>{roomType} ‚ñæ</Text>
                    </TouchableOpacity>
                </View>

                {/* Ê†áÁ≠æ */}
                <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.tagScroll}>
                    {['Ë±™ÂçéÈÖíÂ∫ó', 'ÂÖçË¥πÂÅúËΩ¶Âú∫', 'Âê´Êó©È§ê', 'ËøëÂú∞ÈìÅ', 'Ê∏©Ê≥âÈÖíÂ∫ó'].map((tag) => (
                        <TouchableOpacity
                            key={tag}
                            style={[styles.tagItem, selectedTags.includes(tag) && styles.tagItemActive]}
                            onPress={() => setSelectedTags(prev => prev.includes(tag) ? prev.filter(t => t !== tag) : [...prev, tag])}
                        >
                            <Text style={[styles.tagText, selectedTags.includes(tag) && styles.tagTextActive]}>{tag}</Text>
                        </TouchableOpacity>
                    ))}
                </ScrollView>

                {/* Êü•ËØ¢ÊåâÈíÆ */}
                <TouchableOpacity
                    style={styles.searchButton}
                    onPress={() => navigation.navigate('HotelList', {
                        city, province, price, roomType,
                        startDate: dateInfo.startStr, endDate: dateInfo.endStr
                    })}
                >
                    <Text style={styles.searchButtonText}>Êü•ËØ¢ÈÖíÂ∫ó</Text>
                </TouchableOpacity>
            </View>

            {/* ÈÄöÁî® Modal ÈÄâÊã©Âô® */}
            <Modal visible={modalVisible} animationType="slide" transparent={true}>
                <View style={styles.modalOverlay}>
                    <View style={styles.modalContent}>
                        <View style={styles.modalHeader}>
                            <Text style={styles.modalTitle}>ËØ∑ÈÄâÊã©</Text>
                            <TouchableOpacity onPress={() => setModalVisible(false)}><Text style={{ color: '#999' }}>ÂèñÊ∂à</Text></TouchableOpacity>
                        </View>
                        <FlatList
                            data={getModalData()}
                            keyExtractor={(item) => item}
                            renderItem={({ item }) => (
                                <TouchableOpacity style={styles.modalItem} onPress={() => handleSelect(item)}>
                                    <Text style={styles.modalItemText}>{item}</Text>
                                </TouchableOpacity>
                            )}
                        />
                    </View>
                </View>
            </Modal>

            {/* Êó•ÂéÜÁªÑ‰ª∂ */}
            <Calendar_My
                visible={calendarVisible}
                onClose={() => setCalendarVisible(false)}
                onConfirm={(data) => { setDateInfo(data); setCalendarVisible(false); }}
            />
        </ScrollView>
    );
};

export default HomeScreen;
