import { NativeStackScreenProps } from '@react-navigation/native-stack';
import React from 'react';
import {
    Dimensions,
    Image,
    ScrollView,
    StyleSheet,
    Text,
    TouchableOpacity,
    View
} from 'react-native';
import { RootStackParamList } from '../../navigation/types';

type Props = NativeStackScreenProps<RootStackParamList, 'Home'>;

const { width } = Dimensions.get('window');

const HomeScreen = ({ navigation }: Props) => {

    // 模拟广告点击跳转
    const handleBannerPress = (hotelId: string) => {
        // 假设跳转到详情页，并传递酒店ID
        navigation.navigate('HotelDetail', { hotelId, hotelName: '示例酒店' });
    };

    return (
        <ScrollView style={styles.container} bounces={false}>
            {/* 1. 顶部 Banner 区域 */}
            <TouchableOpacity
                activeOpacity={0.9}
                onPress={() => handleBannerPress('1001')}
                style={styles.bannerContainer}
            >
                <Image
                    source={{ uri: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?auto=format&fit=crop&w=800&q=80' }}
                    style={styles.bannerImage}
                />
                <View style={styles.bannerMask}>
                    <Text style={styles.bannerTitle}>春季特惠：三亚海景房 5 折起</Text>
                </View>
            </TouchableOpacity>

            {/* 2. 查询卡片区域 */}
            <View style={styles.searchCard}>
                {/* 城市选择 */}
                <TouchableOpacity style={styles.searchItem}>
                    <Text style={styles.label}>目的地</Text>
                    <Text style={styles.searchValue}>北京</Text>
                </TouchableOpacity>

                <View style={styles.divider} />

                {/* 日期选择 */}
                <View style={styles.row}>
                    <TouchableOpacity style={[styles.searchItem, { flex: 1 }]}>
                        <Text style={styles.label}>入住日期</Text>
                        <Text style={styles.searchValue}>05月01日</Text>
                    </TouchableOpacity>
                    <View style={styles.verticalDivider} />
                    <TouchableOpacity style={[styles.searchItem, { flex: 1 }]}>
                        <Text style={styles.label}>离店日期</Text>
                        <Text style={styles.searchValue}>05月03日</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.divider} />

                {/* 底部查询按钮 */}
                <TouchableOpacity
                    style={styles.searchButton}
                    onPress={() => navigation.navigate('HotelList', {
                        city: '北京',
                        startDate: '2024-05-01',
                        endDate: '2024-05-03'
                    })}
                >
                    <Text style={styles.searchButtonText}>立即查询</Text>
                </TouchableOpacity>
            </View>

            {/* 3. 补充说明区域 (可选) */}
            <View style={styles.infoSection}>
                <Text style={styles.infoText}>· 官方直营 · 极速确认 · 无忧退改</Text>
            </View>
        </ScrollView>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    // Banner 样式
    bannerContainer: {
        width: width,
        height: 160,
        position: 'relative',
    },
    bannerImage: {
        width: '100%',
        height: '100%',
    },
    bannerMask: {
        position: 'absolute',
        bottom: 0,
        width: '100%',
        padding: 15,
        backgroundColor: 'rgba(0,0,0,0.3)',
    },
    bannerTitle: {
        color: '#fff',
        fontSize: 18,
        fontWeight: 'bold',
    },
    // 搜索卡片样式
    searchCard: {
        margin: 15,
        marginTop: -30, // 向上偏移覆盖在 banner 上
        backgroundColor: '#fff',
        borderRadius: 12,
        padding: 20,
        // 阴影
        elevation: 5,
        shadowColor: '#000',
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 8,
    },
    searchItem: {
        paddingVertical: 12,
    },
    label: {
        fontSize: 12,
        color: '#999',
        marginBottom: 4,
    },
    searchValue: {
        fontSize: 18,
        fontWeight: '600',
        color: '#333',
    },
    row: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    divider: {
        height: 1,
        backgroundColor: '#eee',
        marginVertical: 5,
    },
    verticalDivider: {
        width: 1,
        height: 30,
        backgroundColor: '#eee',
        marginHorizontal: 15,
    },
    searchButton: {
        backgroundColor: '#007AFF',
        borderRadius: 25,
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 20,
    },
    searchButtonText: {
        color: '#fff',
        fontSize: 18,
        fontWeight: 'bold',
    },
    infoSection: {
        alignItems: 'center',
        marginTop: 10,
    },
    infoText: {
        color: '#ccc',
        fontSize: 12,
    }
});

export default HomeScreen;
